// src/pages/AccountPage.jsx
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

const Wrapper = styled.div`
  max-width: 900px;
  margin: 3rem auto;
  padding: 0 1rem;
`;
const Tabs = styled.div`
  display: flex;
  gap: 2rem;
  border-bottom: 2px solid #e0e0e0;
  margin-bottom: 2rem;
`;
const Tab = styled.button`
  background: none;
  border: none;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  padding: 0.6rem 0;
  color: ${p => (p.active === "true" ? "#175dff" : "#777")};
  border-bottom: 3px solid ${p => (p.active === "true" ? "#175dff" : "transparent")};
`;
const Section = styled.div`
  animation: fade 0.3s;
  @keyframes fade { from { opacity: 0 } to { opacity: 1 } }
`;
const FormGroup = styled.div`
  margin-bottom: 1rem;
`;
const Label = styled.label`
  display: block;
  margin-bottom: 0.3rem;
  color: #003b70;
  font-weight: 600;
`;
const Input = styled.input`
  width: 100%;
  padding: 0.6rem;
  border: 1px solid #d1e0ff;
  border-radius: 6px;
`;
const SaveButton = styled.button`
  padding: 0.7rem 1.5rem;
  background: #175dff;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  &:hover { background: #004bff; }
`;
const DeleteButton = styled.button`
  padding: 0.7rem 1.5rem;
  background: #e92365;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-left: 1rem;
  &:hover { background: #ff6b9b; }
`;
const BookingCard = styled.div`
  border: 1px solid #e0e0e0;
  border-radius: 6px;
  padding: 1rem;
  margin-bottom: 1rem;
`;
const ReviewInput = styled.textarea`
  width: 100%;
  min-height: 80px;
  padding: 0.5rem;
  margin-top: 0.5rem;
`;
const SubmitReviewButton = styled.button`
  padding: 0.6rem 1.2rem;
  background: #28a745;
  color: #fff;
  border: none;
  border-radius: 6px;
  margin-top: 0.5rem;
`;

export default function AccountPage() {
  const [tab, setTab] = useState("profile");
  const [user, setUser] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [reviews, setReviews] = useState({});
  const [newReview, setNewReview] = useState({});
  const [editUser, setEditUser] = useState({
    email: "",
    username: "",
    password: "",
    firstName: "",
    lastName: "",
    age: "",
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 1) Load current user on mount
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("No authentication token found. Redirecting to login...");
      setLoading(false);
      setTimeout(() => (window.location.href = "/"), 2000);
      return;
    }

    axios
      .get("/api/users/me", { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        const u = res.data;
        setUser(u);
        setEditUser({
          email: u.email || "",
          username: u.username || "",
          password: "Current password not shown (enter new password to change)",
          firstName: u.firstName || "",
          lastName: u.lastName || "",
          age: u.age ? u.age.toString() : "",
        });
      })
      .catch(err => {
        setError(`Failed to load profile: ${err.message}`);
      })
      .finally(() => setLoading(false));
  }, []);

  // 2) Once we know user.id, fetch their bookings + reviews
  useEffect(() => {
    if (!user?.id) return;
    const token = localStorage.getItem("jwt");
    axios
      .get(`/api/bookings/user/${user.id}`, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        setBookings(res.data);
        // fetch reviews for each booking
        res.data.forEach(bk => {
          axios
            .get(`/api/reviews/booking/${bk.id}`, { headers: { Authorization: `Bearer ${token}` } })
            .then(r => {
              setReviews(prev => ({ ...prev, [bk.id]: r.data }));
            })
            .catch(() => {
              /* ignore review load failures */
            });
        });
      })
      .catch(err => {
        setError(`Failed to load bookings: ${err.message}`);
      });
  }, [user]);

  const handleSave = () => {
    const token = localStorage.getItem("jwt");
    const payload = {
      email: editUser.email,
      username: editUser.username,
      firstName: editUser.firstName,
      lastName: editUser.lastName,
      age: editUser.age ? parseInt(editUser.age, 10) : undefined,
    };
    if (
      editUser.password.trim() &&
      editUser.password !== "Current password not shown (enter new password to change)"
    ) {
      payload.password = editUser.password;
    }

    axios
      .patch("/api/users/me", payload, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        setUser(res.data);
        alert("Profile updated successfully!");
      })
      .catch(err => {
        setError(`Failed to update profile: ${err.message}`);
      });
  };

  const handleDelete = () => {
    if (!window.confirm("Are you sure you want to delete your account?")) return;
    const token = localStorage.getItem("jwt");
    axios
      .delete("/api/users/me", { headers: { Authorization: `Bearer ${token}` } })
      .then(() => {
        localStorage.removeItem("jwt");
        window.location.href = "/";
      })
      .catch(err => {
        setError(`Failed to delete account: ${err.message}`);
      });
  };

  const handleReviewSubmit = bookingId => {
    const token = localStorage.getItem("jwt");
    axios
      .post(
        "/api/reviews",
        {
          bookingId,
          comment: newReview[bookingId] || "",
          rating: 5,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then(res => {
        setReviews(prev => ({
          ...prev,
          [bookingId]: [res.data, ...(prev[bookingId] || [])],
        }));
        setNewReview(prev => ({ ...prev, [bookingId]: "" }));
      })
      .catch(err => {
        setError(`Failed to add review: ${err.message}`);
      });
  };

  if (loading) return <p>Loading...</p>;

  return (
    <Wrapper>
      <h1>My account</h1>
      <Tabs>
        <Tab active={tab === "profile" ? "true" : "false"} onClick={() => setTab("profile")}>
          Profile
        </Tab>
        <Tab active={tab === "bookings" ? "true" : "false"} onClick={() => setTab("bookings")}>
          My bookings
        </Tab>
      </Tabs>

      {error && <p style={{ color: "#e92365" }}>{error}</p>}

      {tab === "profile" && user && (
        <Section>
          <h2>Edit details</h2>
          <FormGroup>
            <Label>Email</Label>
            <Input
              value={editUser.email}
              onChange={e => setEditUser({ ...editUser, email: e.target.value })}
            />
          </FormGroup>
          <FormGroup>
            <Label>Username</Label>
            <Input
              value={editUser.username}
              onChange={e => setEditUser({ ...editUser, username: e.target.value })}
            />
          </FormGroup>
          <FormGroup>
            <Label>Password (enter new to change)</Label>
            <Input
              type="text"
              value={editUser.password}
              onChange={e => setEditUser({ ...editUser, password: e.target.value })}
            />
          </FormGroup>
          <FormGroup>
            <Label>First Name</Label>
            <Input
              value={editUser.firstName}
              onChange={e => setEditUser({ ...editUser, firstName: e.target.value })}
            />
          </FormGroup>
          <FormGroup>
            <Label>Last Name</Label>
            <Input
              value={editUser.lastName}
              onChange={e => setEditUser({ ...editUser, lastName: e.target.value })}
            />
          </FormGroup>
          <FormGroup>
            <Label>Age</Label>
            <Input
              type="number"
              value={editUser.age}
              onChange={e => setEditUser({ ...editUser, age: e.target.value })}
            />
          </FormGroup>
          <FormGroup>
            <Label>Role (read-only)</Label>
            <Input value={user.role} disabled />
          </FormGroup>
          <SaveButton onClick={handleSave}>Save Changes</SaveButton>
          <DeleteButton onClick={handleDelete}>Delete Account</DeleteButton>
        </Section>
      )}

      {tab === "bookings" && (
        <Section>
          <h2>Upcoming & past trips</h2>
          {bookings.length ? (
            bookings.map(booking => (
              <BookingCard key={booking.id}>
                <h3>
                  {booking.listing?.title || "Unknown"} ({booking.type})
                </h3>
                <p>
                  Check-in: {booking.checkIn} | Check-out: {booking.checkOut}
                </p>
                <p>
                  Total: €
                  {booking.listing?.price * (booking.numGuests || 1) || "N/A"}
                </p>
                <p>Status: {booking.status}</p>
                <ReviewInput
                  value={newReview[booking.id] || ""}
                  onChange={e =>
                    setNewReview({ ...newReview, [booking.id]: e.target.value })
                  }
                  placeholder="Add a review..."
                />
                <SubmitReviewButton onClick={() => handleReviewSubmit(booking.id)}>
                  Submit Review
                </SubmitReviewButton>
                {reviews[booking.id]?.length > 0 && (
                  <div>
                    <h4>Reviews</h4>
                    {reviews[booking.id].map(r => (
                      <p key={r.id}>{r.comment}</p>
                    ))}
                  </div>
                )}
              </BookingCard>
            ))
          ) : (
            <p>You have no bookings yet.</p>
          )}
        </Section>
      )}
    </Wrapper>
  );
}
