import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import Header from "../components/Header";

const pageBlue = "#d8edff";
const fadeIn = keyframes`…`;
const zoom   = keyframes`…`;
const accent1 = "#175dff";
const textDark = "#003b70";

const Wrapper = styled.div`…`;
const Hero    = styled.section`…`;
const HeroTitle = styled.h1`…`;
const Grid    = styled.section`…`;
const Card    = styled.div`…`;
const Img     = styled.img`…`;
const Mask    = styled.div`…`;
const Label   = styled.h3`…`;
const CardContent = styled.div`…`;
const Button  = styled.button`…`;

export default function AdminListingsPage() {
  const { category } = useParams();           // "flights", "hotels", or "events"
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    axios.get(`/api/listings/${category}`)
         .then(res => setItems(res.data))
         .catch(err => console.error(err));
  }, [category]);

  const handleEdit = id => navigate(`/admin/${category}/${id}/edit`);
  const handleDelete = async id => {
    const token = localStorage.getItem("jwt");
    await axios.delete(`/api/listings/${category}/${id}`,
                       { headers: { Authorization: `Bearer ${token}` } });
    setItems(prev => prev.filter(i => i.id !== id));
  };

  return (
    <Wrapper>
      <Header />
      <Hero>
        <HeroTitle>Manage {category.charAt(0).toUpperCase() + category.slice(1)}</HeroTitle>
      </Hero>

      <Grid>
        {items.map(item => (
          <Card key={item.id}>
            {item.imageUrl && <Img src={item.imageUrl} alt={item.title} />}
            <Mask />
            <Label>{item.title}</Label>
            <CardContent>
              <Button onClick={() => handleEdit(item.id)}>Edit</Button>
              <Button onClick={() => handleDelete(item.id)}>Delete</Button>
            </CardContent>
          </Card>
        ))}
        <Button onClick={() => navigate(`/admin/${category}/new`)}>Add New</Button>
      </Grid>
    </Wrapper>
  );
}
