import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

const Wrapper = styled.div`max-width:1200px;margin:3rem auto;padding:0 1rem;`;
const Tabs = styled.div`display:flex;gap:2rem;border-bottom:2px solid #e0e0e0;`;
const Tab = styled.button`background:none;border:none;font-size:1.1rem;font-weight:600;cursor:pointer;padding:.6rem 0;color:${p => p.active ? "#175dff" : "#777"};border-bottom:3px solid ${p => p.active ? "#175dff" : "transparent"};`;
const Section = styled.div`animation:fade .3s;@keyframes fade{from{opacity:0}to{opacity:1}};`;
const FormGroup = styled.div`margin-bottom:1rem;`;
const Label = styled.label`display:block;margin-bottom:0.3rem;color:#003b70;font-weight:600;`;
const Input = styled.input`width:100%;padding:0.6rem;border:1px solid #d1e0ff;border-radius:6px;`;
const Button = styled.button`padding:0.7rem 1.5rem;background:#175dff;color:#fff;border:none;border-radius:6px;cursor:pointer;margin-right:1rem;&:hover{background:#004bff;}`;
const Table = styled.table`width:100%;border-collapse:collapse;th,td{padding:0.5rem;border:1px solid #e0e0e0;}`;

export default function AdminPage() {
  const [tab, setTab] = useState("listings");
  const [listings, setListings] = useState([]);
  const [users, setUsers] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("No authentication token found. Redirecting to login...");
      setTimeout(() => (window.location.href = "/login"), 2000);
      return;
    }

    Promise.all([
      axios.get("/api/listings/hotels", { headers: { Authorization: `Bearer ${token}` } }),
      axios.get("/api/users", { headers: { Authorization: `Bearer ${token}` } }),
      axios.get("/api/admin/bookings", { headers: { Authorization: `Bearer ${token}` } }),
      axios.get("/api/reviews/listing/1", { headers: { Authorization: `Bearer ${token}` } }), // Example listing ID
    ])
      .then(([listingsRes, usersRes, bookingsRes, reviewsRes]) => {
        setListings(listingsRes.data);
        setUsers(usersRes.data);
        setBookings(bookingsRes.data);
        setReviews(reviewsRes.data);
        setLoading(false);
      })
      .catch(err => {
        setError("Failed to load data: " + err.message);
        setLoading(false);
      });
  }, []);

  const handleModerateReview = (reviewId, approved, remarks) => {
    const token = localStorage.getItem("jwt");
    axios
      .post(
        `/api/reviews/${reviewId}/moderate`,
        null,
        {
          headers: { Authorization: `Bearer ${token}` },
          params: { approved, remarks },
        }
      )
      .then(() => {
        setReviews(reviews.map(r => (r.id === reviewId ? { ...r, approved, remarks } : r)));
      })
      .catch(err => setError("Failed to moderate review: " + err.message));
  };

  const handleDeleteUser = (id) => {
    const token = localStorage.getItem("jwt");
    axios
      .delete(`/api/users/${id}`, { headers: { Authorization: `Bearer ${token}` } })
      .then(() => setUsers(users.filter(u => u.id !== id)))
      .catch(err => setError("Failed to delete user: " + err.message));
  };

  const handleDeleteBooking = (id) => {
    const token = localStorage.getItem("jwt");
    axios
      .delete(`/api/admin/bookings/${id}`, { headers: { Authorization: `Bearer ${token}` } })
      .then(() => setBookings(bookings.filter(b => b.id !== id)))
      .catch(err => setError("Failed to delete booking: " + err.message));
  };

  if (loading) return <p>Loading...</p>;

  return (
    <Wrapper>
      <h1>Admin Panel</h1>
      {error && <p style={{ color: "#e92365" }}>{error}</p>}
      <Tabs>
        <Tab active={tab === "listings"} onClick={() => setTab("listings")}>
          Listings
        </Tab>
        <Tab active={tab === "users"} onClick={() => setTab("users")}>
          Users
        </Tab>
        <Tab active={tab === "bookings"} onClick={() => setTab("bookings")}>
          Bookings
        </Tab>
        <Tab active={tab === "reviews"} onClick={() => setTab("reviews")}>
          Reviews
        </Tab>
      </Tabs>

      {tab === "listings" && (
        <Section>
          <h2>Manage Listings</h2>
          {/* Add forms for creating hotels, flights, events */}
        </Section>
      )}

      {tab === "users" && (
        <Section>
          <h2>Manage Users</h2>
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>
                    <Button onClick={() => {/* Add update logic */}}>
                      Edit
                    </Button>
                    <Button onClick={() => handleDeleteUser(user.id)}>
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Section>
      )}

      {tab === "bookings" && (
        <Section>
          <h2>Manage Bookings</h2>
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>User ID</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map(booking => (
                <tr key={booking.id}>
                  <td>{booking.id}</td>
                  <td>{booking.userId}</td>
                  <td>{booking.status}</td>
                  <td>
                    <Button onClick={() => {/* Add update logic */}}>
                      Edit
                    </Button>
                    <Button onClick={() => handleDeleteBooking(booking.id)}>
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Section>
      )}

      {tab === "reviews" && (
        <Section>
          <h2>Moderate Reviews</h2>
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Comment</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {reviews.map(review => (
                <tr key={review.id}>
                  <td>{review.id}</td>
                  <td>{review.comment}</td>
                  <td>{review.approved ? "Approved" : "Pending"}</td>
                  <td>
                    <Button onClick={() => handleModerateReview(review.id, true, "Approved by admin")}>
                      Approve
                    </Button>
                    <Button onClick={() => handleModerateReview(review.id, false, "Rejected by admin")}>
                      Reject
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Section>
      )}
    </Wrapper>
  );
}
