import React from "react";
import { Link, useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import { FaPlaneDeparture, FaSearch } from "react-icons/fa";

/* ─── palette ─── */
const headerFrom = "#0048ff", headerTo = "#177dff";
const pageBlue = "#d8edff", textDark = "#003b70";

/* ─── animations ─── */
const fadeIn = keyframes`from{opacity:0;transform:translateY(15px)}
                         to{opacity:1;transform:translateY(0)}`;
const zoom = keyframes`from{transform:scale(1)}to{transform:scale(1.08)}`;

/* ─── layout ─── */
const Wrapper = styled.div`
  min-height:100vh;background:${pageBlue};display:flex;flex-direction:column;
  font-family:"Segoe UI",Tahoma,Verdana,sans-serif;
`;

/* header */
const Header = styled.header`
  position:sticky;top:0;z-index:1000;padding:.9rem 2rem;
  display:flex;align-items:center;justify-content:space-between;
  background:linear-gradient(135deg,${headerFrom},${headerTo});
`;
const Logo = styled(Link)`
  display:flex;align-items:center;gap:.45rem;font-size:1.9rem;
  font-weight:700;color:#fff;text-decoration:none;
`;
const Nav = styled.nav`display:flex;gap:1.6rem;align-items:center;`;
const NavLink = styled(Link)`
  color:#fff;text-decoration:none;font-weight:500;position:relative;
  &::after{content:\"\";position:absolute;bottom:-4px;left:0;width:0;height:2px;
           background:#fff;transition:.3s}
  &:hover::after{width:100%}
`;
const SearchPill = styled.form`
  display:flex;align-items:center;gap:.4rem;padding:.35rem .9rem;
  background:#fff;border-radius:24px;box-shadow:0 2px 6px rgba(0,0,0,.1);
`;
const SearchInput = styled.input`
  border:none;outline:none;font-size:.9rem;width:150px`;

/* hero banner */
const Hero = styled.section`
  background:url("https://source.unsplash.com/1600x700/?beach") center/cover fixed;
  color:#fff;text-align:center;padding:4.5rem 1rem 6rem;
  display:flex;flex-direction:column;align-items:center;justify-content:center;
  animation:${fadeIn}.7s ease-out;
`;
const HeroTitle = styled.h1`font-size:3rem;font-weight:800;margin:0 0 1rem;`;
const HeroSearch = styled(SearchPill)`box-shadow:none;width:310px;margin-top:1.5rem;`;

/* grid */
const Grid = styled.section`
  width:100%;max-width:1400px;margin:-4rem auto 0;
  display:grid;gap:2rem;padding:0 2.5rem 4rem;
  grid-template-columns:repeat(auto-fit,minmax(260px,1fr));animation:${fadeIn}.8s ease-out;
`;
const Card = styled.div`
  position:relative;border-radius:18px;overflow:hidden;height:260px;cursor:pointer;
  box-shadow:0 8px 18px rgba(0,0,0,.15);transition:transform .35s;
  &:hover{transform:translateY(-6px)}
  &:hover img{animation:${zoom} 4s ease-out forwards}
`;
const Img = styled.img`width:100%;height:100%;object-fit:cover;`;
const Mask = styled.div`
  position:absolute;inset:0;background:linear-gradient(to bottom,transparent 40%,rgba(0,0,0,.65));
`;
const Label = styled.h3`
  position:absolute;bottom:1.2rem;left:1.4rem;margin:0;
  color:#fff;font-size:1.5rem;font-weight:600;
`;

/* ─── component ─── */
export default function HomePage(){
  const go = useNavigate();

  const dest = [
    {c:"FR",n:"France", img:"https://source.unsplash.com/800x600/?paris"},
    {c:"ES",n:"Spain",  img:"https://source.unsplash.com/800x600/?barcelona"},
    {c:"IT",n:"Italy",  img:"https://source.unsplash.com/800x600/?venice"},
    {c:"GR",n:"Greece", img:"https://source.unsplash.com/800x600/?santorini"},
    {c:"JP",n:"Japan",  img:"https://source.unsplash.com/800x600/?tokyo"},
    {c:"US",n:"USA",    img:"https://source.unsplash.com/800x600/?new%20york"},
    {c:"BR",n:"Brazil", img:"https://source.unsplash.com/800x600/?rio"},
    {c:"TH",n:"Thailand",img:"https://source.unsplash.com/800x600/?phuket"},
  ];

  return (
  <Wrapper>
    {/* header */}
    <Header>
      <Logo to="/home">
        <FaPlaneDeparture /> Bookify
      </Logo>

      <Nav>
        <NavLink to="/home">Packages</NavLink>
        <NavLink to="/hotels">Hotels</NavLink>
        <NavLink to="/flights">Flights</NavLink>
        <NavLink to="/events">Events</NavLink>
      </Nav>

      <SearchPill onSubmit={e => e.preventDefault()}>
        <FaSearch size={14} />
        <SearchInput placeholder="Search" />
      </SearchPill>
    </Header>

    {/* hero */}
    <Hero>
      <HeroTitle>Where will you go next?</HeroTitle>
      <p style={{ fontSize: "1.15rem", maxWidth: 640 }}>
        Discover bundled getaways – flight, hotel &amp; experience in one click.
      </p>

      <HeroSearch onSubmit={e => e.preventDefault()}>
        <FaSearch size={14} color={textDark} />
        <SearchInput placeholder='Try "Paris"' />
      </HeroSearch>
    </Hero>

    {/* destination grid */}
    <Grid>
      {dest.map(d => (
        <Card key={d.c} onClick={() => go(`/destinations/${d.c}`)}>
          <Img src={d.img} alt={d.n} />
          <Mask />
          <Label>{d.n}</Label>
        </Card>
      ))}
    </Grid>
  </Wrapper>
);
}
