/* src/pages/PackagePage.jsx */
import React from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";

const Wrapper = styled.div`max-width:1000px;margin:2rem auto;padding:0 1.5rem;font-family:"Segoe UI",sans-serif;`;
const Hero    = styled.img`width:100%;border-radius:22px;max-height:360px;object-fit:cover;`;
const H2      = styled.h2`margin:1.2rem 0 .6rem;color:#003b70;`;
const Box     = styled.div`background:#fff;border-radius:14px;padding:1.4rem;box-shadow:0 6px 18px rgba(0,0,0,.12);margin-bottom:1.6rem;`;
const Label   = styled.span`display:inline-block;font-weight:700;color:#175dff;margin-right:.6rem;`;
const BookBtn = styled.button`
  display:block;margin:1.2rem 0 0 auto;padding:.9rem 1.8rem;border:none;
  border-radius:30px;background:#175dff;color:#fff;font-weight:600;font-size:1rem;
  cursor:pointer;transition:.25s; &:hover{transform:translateY(-2px);box-shadow:0 8px 24px rgba(23,93,255,.45);}
`;

export default function PackagePage(){
  const { state:data } = useLocation();   // contains the bundle sent from the list
  if(!data) return <p style={{textAlign:"center"}}>No package data.</p>;

  const {hotel,flight,event,total,nights}=data;

  return(
    <Wrapper>
      <Hero src={data.thumb}/>
      <H2>{hotel.title}</H2>

      <Box>
        <Label>Hotel</Label>{hotel.city} · {nights} nights · €{(nights*hotel.pricePerNight).toFixed(0)}
        <br/>{hotel.description}
      </Box>

      <Box>
        <Label>Flight</Label>{flight.departure} → {flight.arrival}
        <br/>Departs: {new Date(flight.departureTime).toLocaleString()}
        <br/>Returns: {new Date(flight.arrivalTime).toLocaleString()}
        <br/>€{flight.price}
      </Box>

      <Box>
        <Label>Event</Label>{event.title} – {new Date(event.eventDate).toLocaleString()}
        <br/>Venue: {event.venue} · €{event.price}
      </Box>

      <H2>Total: €{total.toFixed(0)}</H2>
      <BookBtn onClick={()=>alert("✅ Booking created (demo)")}>Book now</BookBtn>
    </Wrapper>
  );
}
