// src/pages/CategoryPage.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import { FaCalendarAlt } from "react-icons/fa";
import Header from "../components/Header";     // ← adjust if your path differs

/* ─── tiny helpers ────────────────────────────────────────────────────────── */
const zoomIn = keyframes`
  from { opacity: 0; transform: translateY(15px) }
  to   { opacity: 1; transform: translateY(0)   }
`;

const Wrapper = styled.div`
  padding: 2rem;
  animation: ${zoomIn} 0.5s ease-out;
`;
const FilterBar = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 1.5rem;
  align-items: center;
`;
const Select    = styled.select`padding: .5rem;font-size:1rem;`;
const DateInput = styled.input `padding: .5rem;font-size:1rem;`;

const Grid = styled.div`
  display: grid;
  gap: 1.5rem;
  grid-template-columns: repeat(auto-fit,minmax(240px,1fr));
`;

const Card = styled.div`
  position: relative;
  border-radius: 12px;
  overflow: hidden;
  cursor: pointer;
  box-shadow: 0 4px 12px rgba(0,0,0,.1);
  transition: transform .25s;
  &:hover { transform: translateY(-4px); }
`;
const Img   = styled.img`width:100%;height:160px;object-fit:cover;`;
const Info  = styled.div`padding:1rem;background:#fff;`;
const Title = styled.h3`margin:0 0 .4rem;font-size:1.2rem;`;
const Detail= styled.p `margin:.2rem 0;font-size:.95rem;color:#333;`;

/* ─── main component ──────────────────────────────────────────────────────── */
export default function CategoryPage({ category }) {
  const [items,     setItems]     = useState([]);
  const [countries, setCountries] = useState([]);
  const [filterCountry, setFilterCountry] = useState("");
  const [filterDate,    setFilterDate]    = useState("");
  const navigate = useNavigate();

  /* fetch list secured by JWT */
  useEffect(() => {
    (async () => {
      const token = localStorage.getItem("jwt");
      if (!token) {
        alert("You need to log in to browse " + category + ".");
        return;
      }

      const res = await fetch(`/api/${category}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.status === 401) {
        alert("Session expired – please log in again.");
        return;
      }
      if (!res.ok) {
        const txt = await res.text();
        throw new Error(txt || res.statusText);
      }

      const data = await res.json();
      setItems(data);

      /* unique country list for the filter */
      setCountries([...new Set(data.map(i => i.country))]);
    })().catch(err => {
      console.error(err);
      alert("Could not load " + category + " – please try later.");
    });
  }, [category]);

  const visible = items.filter(i =>
    (!filterCountry || i.country === filterCountry) &&
    (!filterDate   || new Date(filterDate) >= new Date(i.availableFrom ?? i.eventDate ?? i.departureTime))
  );

  return (
    <>
      <Header />
      <Wrapper>

        {/* ─── filters ──────────────────────────────── */}
        <FilterBar>
          <Select value={filterCountry}
                  onChange={e => setFilterCountry(e.target.value)}>
            <option value="">All countries</option>
            {countries.map(c => (
              <option key={c} value={c}>{c}</option>
            ))}
          </Select>

          <DateInput type="date"
                     value={filterDate}
                     onChange={e => setFilterDate(e.target.value)} />
          <FaCalendarAlt />
        </FilterBar>

        {/* ─── catalogue grid ──────────────────────── */}
        <Grid>
          {visible.map(item => (
            <Card key={item.id}
                  onClick={() => navigate(`/book/${category}/${item.id}`)}>
              <Img src={item.imageUrl || item.photo || "/placeholder.jpg"}
                   alt={item.title} />

              <Info>
                <Title>{item.title}</Title>

                {category === "flights" && (
                  <>
                    <Detail>{item.departure} → {item.arrival}</Detail>
                    <Detail>Seats left: {item.seatCapacity}</Detail>
                  </>
                )}

                {category === "hotels" && (
                  <>
                    <Detail>{item.city}</Detail>
                    <Detail>Rooms available: {item.totalRooms}</Detail>
                  </>
                )}

                {category === "events" && (
                  <>
                    <Detail>{new Date(item.eventDate)
                              .toLocaleDateString()}</Detail>
                    <Detail>Venue: {item.venue}</Detail>
                  </>
                )}
              </Info>
            </Card>
          ))}
        </Grid>
      </Wrapper>
    </>
  );
}
