import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";

/**
 * AuthPage â€“ singleâ€‘file auth flow (login â–¸ signup â–¸ forgotâ€‘password)
 *   â€¢ Ocean image left
 *   â€¢ Soft babyâ€‘blue background on the right
 *   â€¢ Deeper blue auth card â€“ no white fill anywhere
 */

/* Palette */
const pageBlue = "#d8edff";   // rightâ€‘side page background
const cardBlue = "#b4d0ff";   // auth card background
const inputBlue = "#e6f2ff";  // input background
const accentBlue1 = "#175dff"; // button gradient start / links
const accentBlue2 = "#1c8bff"; // button gradient end
const textDark = "#003b70";

/********************************************************************
 * Layout + Styled Components
 *******************************************************************/
const Wrapper = styled.div`
  display: flex;
  min-height: 100vh;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
`;

const ImageSide = styled.div`
  flex: 1;
  background-image: url("https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1352&q=80");
  background-size: cover;
  background-position: center;
`;

const FormSide = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 3rem;
  background: ${pageBlue};
`;

const Card = styled.div`
  width: 100%;
  max-width: 420px;
  border-radius: 20px;
  background: ${cardBlue};
  box-shadow: 0 12px 28px rgba(0, 59, 112, 0.18);
  padding: 3rem 2.75rem;
`;

const Title = styled.h2`
  margin: 0 0 1.75rem 0;
  font-size: 2rem;
  color: ${textDark};
  text-align: center;
`;

const StyledInput = styled.input`
  width: 100%;
  margin-bottom: 1.2rem;
  padding: 0.9rem 1rem;
  border: 1px solid rgba(0, 0, 0, 0.05);
  border-radius: 8px;
  font-size: 1rem;
  background: ${inputBlue};
  color: ${textDark};
  &::placeholder { color: #4d6b9a; }
  &:focus {
    outline: none;
    border-color: ${accentBlue1};
    box-shadow: 0 0 0 3px rgba(23, 93, 255, 0.22);
  }
`;

const Button = styled.button`
  width: 100%;
  padding: 1rem;
  background: linear-gradient(135deg, ${accentBlue1}, ${accentBlue2});
  border: none;
  border-radius: 8px;
  color: #fff;
  font-size: 1.05rem;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 22px rgba(23, 93, 255, 0.35);
  }
  &:active {
    transform: translateY(0);
    box-shadow: none;
  }
`;

const TextLink = styled.span`
  color: ${accentBlue1};
  text-decoration: underline;
  cursor: pointer;
`;

const Small = styled.small`
  display: block;
  margin-top: 1.25rem;
  font-size: 0.9rem;
  text-align: center;
  color: ${textDark};
`;

const Error = styled.div`
  margin-bottom: 1rem;
  padding: 0.85rem 1rem;
  background: #ffe6e6;
  color: #b00020;
  border-radius: 8px;
  font-size: 0.95rem;
`;

const Success = styled.div`
  margin-bottom: 1rem;
  padding: 0.85rem 1rem;
  background: #e4ffea;
  color: #0e7a29;
  border-radius: 8px;
  font-size: 0.95rem;
`;

/********************************************************************
 * Component
 *******************************************************************/
function AuthPage() {
  /**
   * mode: "login" | "signup" | "forgot"
   */
  const [mode, setMode] = useState("login");
  const [form, setForm] = useState({});
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const resetMessages = () => {
    setError("");
    setSuccess("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    resetMessages();

    try {
      if (mode === "login") {
        const { data: token } = await axios.post("/api/auth/login", form);
        localStorage.setItem("jwt", token);
        // ðŸ‘‰  TODO: navigate to dashboard
      } else if (mode === "signup") {
        await axios.post("/api/auth/signup", form);
        setSuccess("Account created! You can log in now.");
        setMode("login");
        setForm({});
      } else if (mode === "forgot") {
        await axios.post("/api/auth/forgot-password", null, {
          params: { email: form.email },
        });
        setSuccess(
          "If that email exists, a reset link is on its way to your inbox."
        );
      }
    } catch (err) {
      setError(err.response?.data || "Something went wrong. Please try again.");
    }
  };

  const renderInputs = () => {
    switch (mode) {
      case "signup":
        return (
          <>
            <StyledInput
              name="email"
              type="email"
              placeholder="Email"
              required
              onChange={handleChange}
            />
            <StyledInput
              name="username"
              placeholder="Username"
              required
              onChange={handleChange}
            />
            <StyledInput
              name="password"
              type="password"
              placeholder="Password"
              required
              onChange={handleChange}
            />
          </>
        );
      case "forgot":
        return (
          <StyledInput
            name="email"
            type="email"
            placeholder="Your account email"
            required
            onChange={handleChange}
          />
        );
      default:
        /* login */
        return (
          <>
            <StyledInput
              name="username"
              placeholder="Username"
              required
              onChange={handleChange}
            />
            <StyledInput
              name="password"
              type="password"
              placeholder="Password"
              required
              onChange={handleChange}
            />
          </>
        );
    }
  };

  /** UI Text helpers */
  const titleMap = {
    login: "Welcome back",
    signup: "Create account",
    forgot: "Forgot password",
  };

  const buttonMap = {
    login: "Log in",
    signup: "Sign up",
    forgot: "Send reset link",
  };

  return (
    <Wrapper>
      <ImageSide />
      <FormSide>
        <Card>
          <Title>{titleMap[mode]}</Title>

          {error && <Error>{error}</Error>}
          {success && <Success>{success}</Success>}

          <form onSubmit={handleSubmit}>
            {renderInputs()}

            {/* Extra link only visible in login mode */}
            {mode === "login" && (
              <Small style={{ textAlign: "right", marginBottom: "1rem" }}>
                <TextLink onClick={() => { setMode("forgot"); setForm({}); resetMessages(); }}>
                  Forgot password?
                </TextLink>
              </Small>
            )}

            <Button type="submit">{buttonMap[mode]}</Button>
          </form>

          {/* Switch links */}
          {mode !== "signup" && (
            <Small>
              New here? {" "}
              <TextLink onClick={() => { setMode("signup"); setForm({}); resetMessages(); }}>
                Create one
              </TextLink>
            </Small>
          )}

          {mode !== "login" && (
            <Small>
              Already have an account? {" "}
              <TextLink onClick={() => { setMode("login"); setForm({}); resetMessages(); }}>
                Log in
              </TextLink>
            </Small>
          )}
        </Card>
      </FormSide>
    </Wrapper>
  );
}

export default AuthPage;
