// src/pages/AccountPage.jsx
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import { FaStar } from "react-icons/fa";

/* ---------- styled bits (unchanged) ---------- */
const Wrapper = styled.div`max-width:900px;margin:3rem auto;padding:0 1rem;`;
const Tabs    = styled.div`display:flex;gap:2rem;border-bottom:2px solid #e0e0e0;margin-bottom:2rem;`;
const Tab     = styled.button`
  background:none;border:none;font-size:1.1rem;font-weight:600;cursor:pointer;
  padding:.6rem 0;color:${p=>p.active==="true"?"#175dff":"#777"};
  border-bottom:3px solid ${p=>p.active==="true"?"#175dff":"transparent"};
`;
const Section = styled.div`animation:fade .3s;@keyframes fade{from{opacity:0}to{opacity:1}}`;
const FormGroup    = styled.div`margin-bottom:1rem;`;
const Label        = styled.label`display:block;margin-bottom:.3rem;color:#003b70;font-weight:600;`;
const Input        = styled.input`width:100%;padding:.6rem;border:1px solid #d1e0ff;border-radius:6px;`;
const Textarea     = styled.textarea`width:100%;padding:.6rem;border:1px solid #e0e0e0;border-radius:6px;resize:vertical;`;
const SaveButton   = styled.button`padding:.7rem 1.5rem;background:#175dff;color:#fff;border:none;border-radius:6px;cursor:pointer;&:hover{background:#004bff;}`;
const DeleteButton = styled.button`padding:.7rem 1.5rem;background:#e92365;color:#fff;border:none;border-radius:6px;cursor:pointer;margin-left:1rem;&:hover{background:#ff6b9b;}`;
const BookingCard  = styled.div`border:1px solid #e0e0e0;border-radius:6px;padding:1rem;margin-bottom:1rem;`;
const CancelBtn    = styled.button`padding:.6rem 1.2rem;background:#ffae00;color:#fff;border:none;border-radius:6px;margin-top:.5rem;cursor:pointer;&:hover{background:#ff9100;}`;
const SubmitReviewButton = styled.button`padding:.6rem 1.2rem;background:#28a745;color:#fff;border:none;border-radius:6px;margin-top:.5rem;cursor:pointer;&:hover{background:#218838;}`;
const StarButton   = styled.span`cursor:pointer;font-size:1.4rem;margin-right:0.2rem;`;
const DownloadBtn  = styled.button`margin-right:.5rem;padding:.4rem .8rem;border:none;border-radius:4px;cursor:pointer;background:#0069d9;color:#fff;&:hover{background:#0053ba;}`;

/* ---------- component ---------- */
export default function AccountPage() {
  const [tab, setTab]           = useState("profile");
  const [user, setUser]         = useState(null);
  const [editUser, setEditUser] = useState({});
  const [bookings, setBookings] = useState([]);
  const [reviews, setReviews]   = useState({});    // { bookingId: [ {...}, ... ] }
  const [payments, setPayments] = useState({});   // { bookingId: [ {...}, ... ] }
  const [newReview, setNewReview]   = useState({});
  const [newRating, setNewRating]   = useState({});
  const [loading, setLoading]       = useState(true);
  const [error, setError]           = useState(null);

  const cfg = () => ({ headers:{ Authorization:`Bearer ${localStorage.getItem("jwt")}` } });

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("Please log in.");
      setLoading(false);
      return;
    }

    // 1) load profile
    axios.get("/api/users/me", cfg())
      .then(r => {
        setUser(r.data);
        setEditUser({
          email: r.data.email,
          username: r.data.username,
          password: "Current password not shown",
          firstName: r.data.firstName,
          lastName: r.data.lastName,
          age: r.data.age?.toString()||""
        });
        return r.data.role;
      })
      // 2) load bookings
      .then(role => {
        const url = role==="ADMIN"
          ? "/api/bookings/admin/bookings"
          : "/api/bookings/me";
        return axios.get(url, cfg());
      })
      .then(r => {
        setBookings(r.data);
        // for each booking, fetch payments & reviews
        r.data.forEach(bk => {
          // payments always
          axios.get(`/api/payments/booking/${bk.id}`, cfg())
            .then(res => setPayments(prev=>({
              ...prev, [bk.id]: res.data
            })))
            .catch(()=>{});

          // reviews only for non-admin
          if (user?.role !== "ADMIN") {
            axios.get(`/api/reviews/booking/${bk.id}`, cfg())
              .then(res => setReviews(prev=>({
                ...prev, [bk.id]: res.data
              })))
              .catch(()=>{});
          }
        });
      })
      .catch(e => setError("Load failed: "+e.message))
      .finally(()=>setLoading(false));
  }, []);

  // download handler
  function downloadReceipt(format, bookingId) {
    const token = localStorage.getItem("jwt");
    fetch(`/api/payments/booking/${bookingId}/receipt?format=${format}`, {
      headers: { Authorization:`Bearer ${token}` }
    })
    .then(res => {
      const cd = res.headers.get("content-disposition") || "";
      let filename = `receipt_${bookingId}.${format}`;
      const m = /filename="([^"]+)"/.exec(cd);
      if (m) filename = m[1];
      return res.blob().then(blob=>({blob,filename}));
    })
    .then(({blob,filename}) => {
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url; a.download = filename;
      a.click();
      URL.revokeObjectURL(url);
    });
  }

  const handleReviewSubmit = bookingId => {
    const comment = (newReview[bookingId]||"").trim();
    const rating  = newRating[bookingId] || 5;
    if (!comment) return;
    axios.post("/api/reviews", {
      bookingId, comment, rating
    }, cfg())
    .then(res => {
      setReviews(prev=>({
        ...prev,
        [bookingId]: [...(prev[bookingId]||[]), res.data]
      }));
      setNewReview(prev=>({...prev, [bookingId]:""}));
      setNewRating(prev=>({...prev, [bookingId]:0}));
    })
    .catch(e => setError("Review failed: "+e.message));
  };

  const handleCancel = id => {
    if (!window.confirm("Cancel this booking?")) return;
    const call = user.role==="ADMIN"
      ? axios.delete(`/api/bookings/admin/bookings/${id}`, cfg())
      : axios.post(`/api/bookings/${id}/cancel`, null, cfg());
    call.then(()=> setBookings(bs=>bs.filter(b=>b.id!==id)))
        .catch(e=>setError("Cancel failed: "+e.message));
  };

  const handleSave = () => {
    const payload = {
      email: editUser.email,
      username: editUser.username,
      firstName: editUser.firstName,
      lastName: editUser.lastName,
      age: editUser.age?parseInt(editUser.age,10):undefined
    };
    if (editUser.password !== "Current password not shown")
      payload.password = editUser.password;
    axios.patch("/api/users/me", payload, cfg())
      .then(r=>{ setUser(r.data); alert("Profile updated!"); })
      .catch(e=>setError("Update failed: "+e.message));
  };

  const handleDeleteAccount = () => {
    if (!window.confirm("Delete account?")) return;
    axios.delete("/api/users/me", cfg())
      .then(()=>{
        localStorage.removeItem("jwt");
        window.location.href="/";
      })
      .catch(e=>setError("Delete failed: "+e.message));
  };
  function downloadReceipt(format, bookingId) {
   axios.get(`/api/payments/booking/${bookingId}`, cfg())
     .then(res => {
       const payments = res.data;
       let content, filename, mime;

       if (format === "json") {
         content = JSON.stringify(payments, null, 2);
         filename = `receipt-${bookingId}.json`;
         mime = "application/json";
       } else if (format === "txt") {
         // flatten into plain text
         content = payments.map(p =>
           `Booking ${bookingId}\n` +
           `Amount: â‚¬${p.amount}\n` +
           `Transaction: ${p.transactionId}\n` +
           `Paid at: ${new Date(p.paidAt).toLocaleString()}\n`
         ).join("\n---\n");
         filename = `receipt-${bookingId}.txt`;
         mime = "text/plain";
       } else {
         return alert("Unknown format: " + format);
       }

       const blob = new Blob([content], { type: mime });
       const link = document.createElement("a");
       link.href = URL.createObjectURL(blob);
       link.download = filename;
       link.click();
     })
     .catch(() => alert("Failed to fetch payments"));
 }

  if (loading) return <p>Loadingâ€¦</p>;

  return (
    <Wrapper>
      <h1>My account</h1>
      <Tabs>
        <Tab active={(tab==="profile").toString()}  onClick={()=>setTab("profile")}>Profile</Tab>
        <Tab active={(tab==="bookings").toString()} onClick={()=>setTab("bookings")}>
          {user.role==="ADMIN" ? "Bookings on Your Listings" : "My bookings"}
        </Tab>
      </Tabs>

      {error && <p style={{color:"#e92365"}}>{error}</p>}

      {tab==="profile" && user && (
        <Section>
          {[
            ["Email","email"],["Username","username"],
            ["Password (change)","password","text"],
            ["First name","firstName"],["Last name","lastName"],
            ["Age","age","number"]
          ].map(([lbl,key,type="text"])=>(
            <FormGroup key={key}>
              <Label>{lbl}</Label>
              <Input
                type={type}
                value={editUser[key]||""}
                onChange={e=>setEditUser({...editUser,[key]:e.target.value})}
              />
            </FormGroup>
          ))}
          <FormGroup>
            <Label>Role</Label>
            <Input value={user.role} disabled/>
          </FormGroup>
          <SaveButton onClick={handleSave}>Save changes</SaveButton>
          <DeleteButton onClick={handleDeleteAccount}>Delete account</DeleteButton>
        </Section>
      )}

      {tab==="bookings" && (
        <Section>
          <h2>{user.role==="ADMIN" ? "Bookings on Your Listings" : "Your Trips"}</h2>
          {bookings.length ? bookings.map(bk=>(
            <BookingCard key={bk.id}>
              <h3>{bk.listingTitle} ({bk.type})</h3>
              <p>Check-in: {bk.checkIn} | Check-out: {bk.checkOut}</p>
              <p>Guests: {bk.numGuests}</p>
              <p>Status: {bk.status}</p>
              {bk.listingPrice && (
                <p>Total â‰ˆ â‚¬{(bk.listingPrice * (bk.numGuests||1)).toFixed(0)}</p>
              )}

              {/* payments */}
              <div style={{marginTop:"1rem"}}>
                <h4>Payments</h4>
               {Array.isArray(payments[bk.id]) && payments[bk.id].length > 0
   ? payments[bk.id].map(p => (
       <div key={p.id}>
         <p>Amount: â‚¬{p.amount}</p>
         <p>Transaction: {p.transactionId}</p>
         <p>Paid at: {new Date(p.paidAt).toLocaleString()}</p>
       </div>
     ))
   : <p><em>No payments</em></p>
 }
                <DownloadBtn onClick={()=>downloadReceipt("json",bk.id)}>
                  Download JSON
                </DownloadBtn>
                <DownloadBtn onClick={()=>downloadReceipt("txt", bk.id)}>
                  Download TXT
                </DownloadBtn>
              </div>
  
              {/* admin sees who booked */}
              {user.role==="ADMIN" && bk.createdBy && (
                <div style={{marginTop:"1rem"}}>
                  <p>
                    Booked by: {bk.createdBy.firstName} {bk.createdBy.lastName}
                    {" "}({bk.createdBy.username})
                  </p>
                  <p>Contact: {bk.createdBy.email}</p>
                </div>
              )}

              {/* user review form */}
              {user.role!=="ADMIN" && bk.status!=="CANCELLED" && (
                <div style={{marginTop:"1rem"}}>
                  <div>
                    {[1,2,3,4,5].map(n=>(
                      <StarButton key={n}
                        onClick={()=>setNewRating(r=>({...r,[bk.id]:n}))}
                      >
                        <FaStar
                          color={(newRating[bk.id]||0)>=n ? "#ffc107":"#e4e5e9"}
                        />
                      </StarButton>
                    ))}
                  </div>
                  <Textarea
                    rows={2}
                    placeholder="Add a reviewâ€¦"
                    value={newReview[bk.id]||""}
                    onChange={e=>setNewReview(r=>({...r,[bk.id]:e.target.value}))}
                  />
                  <SubmitReviewButton
                    onClick={()=>handleReviewSubmit(bk.id)}
                  >Submit review</SubmitReviewButton>
                </div>
              )}

              {/* existing reviews */}
              {reviews[bk.id]?.length>0 && (
                <div style={{marginTop:"1rem"}}>
                  <h4>Reviews</h4>
                  {reviews[bk.id].map(rv=>(
                    <div key={rv.id} style={{marginBottom:".5rem"}}>
                      <p>
                        <strong>{rv.author}</strong>: {rv.comment}
                        {" "}(<em>{rv.rating}â˜…</em>)
                      </p>
                      {rv.status==="REJECTED" && rv.moderatorComment && (
                        <p style={{color:"#e92365"}}>
                          <strong>Moderator:</strong> {rv.moderatorComment}
                        </p>
                      )}
                    </div>
                  ))}
                </div>
              )}

              {/* cancel */}
              {bk.status!=="CANCELLED" && (
                <CancelBtn onClick={()=>handleCancel(bk.id)}>
                  Cancel booking
                </CancelBtn>
              )}
            </BookingCard>
          )) : <p>No bookings to show.</p>}
        </Section>
      )}
    </Wrapper>
  );
}
