import React, { useEffect, useState, useMemo } from "react";
import { useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* ------------------------------------------------------------------
   CountryPage – builds bundles purely on the *existing* listings APIs
   ------------------------------------------------------------------
   • GET /api/listings/hotels   → [{ id, country, city, pricePerNight,  photoUrl, availableFrom, availableTo }]
   • GET /api/listings/flights  → [{ id, country, price, departDate, returnDate, airline, photoUrl }]
   • GET /api/listings/events   → [{ id, country, price, eventDate, title, photoUrl }]

   The page:
   1.  fetches the 3 lists once
   2.  filters them by date‑range & country in the browser
   3.  cross‑joins into simple “packages” (1 flight + 1 hotel + 1 event)
       – nights = (#days in range)
       – totalPrice = hotel.pricePerNight*nights + flight.price + event.price
   4.  sorts by totalPrice and shows the cheapest 12.

   (For real‑world usage you’d push the heavy logic server‑side, but this
    meets your “use existing APIs only” constraint.)
   ------------------------------------------------------------------ */

/* style helpers */
const fadeIn = keyframes`from{opacity:0;transform:translateY(10px)}to{opacity:1;transform:translateY(0)}`;
const Hero   = styled.section`background:url(${p=>p.img}) center/cover;height:240px;color:#fff;
  display:flex;align-items:center;justify-content:center;font-size:2.2rem;font-weight:800;`;
const Filters = styled.div`background:#fff;padding:1rem 1.5rem;box-shadow:0 4px 12px rgba(0,0,0,.12);
  border-radius:14px;display:flex;gap:2rem;align-items:flex-start;width:90%;max-width:980px;
  margin:-32px auto 2rem;animation:${fadeIn}.4s ease-out;`;
const Grid   = styled.section`display:grid;gap:2rem;padding:0 2rem 3rem;max-width:1300px;margin:0 auto;
  grid-template-columns:repeat(auto-fit,minmax(280px,1fr));animation:${fadeIn}.5s ease-out;`;
const Card   = styled.div`border-radius:16px;overflow:hidden;box-shadow:0 6px 16px rgba(0,0,0,.12);background:#fff;`; 
const Img    = styled.img`width:100%;height:150px;object-fit:cover;`;
const CBody  = styled.div`padding:1rem;display:flex;flex-direction:column;gap:.4rem;`;
const Price  = styled.span`font-weight:700;font-size:1.05rem;color:#175dff;`;

/* helpers */
const diffDays = (a,b)=>Math.max(1, Math.round((b-a)/(1000*3600*24)));
const iso = d=>d.toISOString().split("T")[0];

export default function CountryPage(){
  const { code }=useParams();
  const [range,setRange]=useState([{startDate:new Date(),endDate:new Date(),key:"sel"}]);
  const [hotels,setHotels]=useState([]);   // raw API arrays
  const [flights,setFlights]=useState([]);
  const [events,setEvents]=useState([]);

  /* load listings once */
  useEffect(()=>{
    const load=async()=>{
      const [h,f,e]=await Promise.all([
        axios.get("/api/listings/hotels"),
        axios.get("/api/listings/flights"),
        axios.get("/api/listings/events")
      ]);
      setHotels(h.data);setFlights(f.data);setEvents(e.data);
    };
    load();
  },[]);

  /* memo‑create packages every time data or range changes */
  const packages = useMemo(()=>{
    if(!hotels.length||!flights.length||!events.length) return [];
    const from=range[0].startDate, to=range[0].endDate;
    const nights = diffDays(from,to);

    const hFilt = hotels.filter(h=>h.country===code && new Date(h.availableFrom)<=from && new Date(h.availableTo)>=to);
    const fFilt = flights.filter(f=>f.country===code && new Date(f.departDate)>=from && new Date(f.returnDate)<=to);
    const eFilt = events.filter(ev=>ev.country===code && new Date(ev.eventDate)>=from && new Date(ev.eventDate)<=to);

    const out=[];
    hFilt.forEach(h=>{
      fFilt.forEach(f=>{
        eFilt.forEach(ev=>{
          out.push({
            id:`${h.id}-${f.id}-${ev.id}`,
            hotel:h,flight:f,event:ev,
            nights,total:nights*h.pricePerNight + f.price + ev.price,
            thumb:h.photoUrl||f.photoUrl||ev.photoUrl||"https://via.placeholder.com/400x250"
          });
        });
      });
    });
    return out.sort((a,b)=>a.total-b.total).slice(0,12);
  },[code,hotels,flights,events,range]);

  /* hero fallback photo */
  const heroImg=`https://images.unsplash.com/1600x500/?${code}&auto=format`; // you can override

  return(
    <>
      <Hero img={heroImg}>{code}</Hero>

      <Filters>
        <div>
          <strong>Dates</strong>
          <DateRange ranges={range}
            onChange={i=>setRange([i.selection])}
            moveRangeOnFirstSelection={false}
            rangeColors={["#175dff"]} editableDateInputs
          />
        </div>
        {/* space for future price slider / category checkboxes */}
      </Filters>

      <Grid>
        {packages.map(p=>(
          <Card key={p.id}>
            <Img src={p.thumb} alt="pkg"/>
            <CBody>
              <div><strong>{p.hotel.name}</strong> + flight + event</div>
              <div>{p.nights} nights · {p.hotel.city}</div>
              <div>Flight: {p.flight.airline}</div>
              <div>Event: {p.event.title}</div>
              <Price>€ {p.total.toFixed(0)}</Price>
            </CBody>
          </Card>
        ))}
        {!packages.length && <p style={{gridColumn:"1/-1",color:textDark}}>No matching packages. Try different dates.</p>}
      </Grid>
    </>
  );
}
