import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

/* ---------- styled bits ---------- */
const Wrapper = styled.div`max-width:900px;margin:3rem auto;padding:0 1rem;`;
const Tabs    = styled.div`display:flex;gap:2rem;border-bottom:2px solid #e0e0e0;margin-bottom:2rem;`;
const Tab     = styled.button`
  background:none;border:none;font-size:1.1rem;font-weight:600;cursor:pointer;
  padding:.6rem 0;color:${p=>p.active==="true"?"#175dff":"#777"};
  border-bottom:3px solid ${p=>p.active==="true"?"#175dff":"transparent"};
`;
const Section = styled.div`animation:fade .3s;@keyframes fade{from{opacity:0}to{opacity:1}}`;
const FormGroup = styled.div`margin-bottom:1rem;`;
const Label = styled.label`display:block;margin-bottom:0.3rem;color:#003b70;font-weight:600;`;
const Input = styled.input`width:100%;padding:0.6rem;border:1px solid #d1e0ff;border-radius:6px;`;
const SaveButton = styled.button`
  padding:0.7rem 1.5rem;background:#175dff;color:#fff;border:none;border-radius:6px;cursor:pointer;
  &:hover{background:#004bff;}
`;
const DeleteButton = styled.button`
  padding:0.7rem 1.5rem;background:#e92365;color:#fff;border:none;border-radius:6px;cursor:pointer;margin-left:1rem;
  &:hover{background:#ff6b9b;}
`;
const BookingCard = styled.div`border:1px solid #e0e0e0;border-radius:6px;padding:1rem;margin-bottom:1rem;`;
const ReviewInput = styled.textarea`width:100%;min-height:80px;padding:0.5rem;margin-top:0.5rem;`;
const SubmitReviewButton = styled.button`
  padding:0.6rem 1.2rem;background:#28a745;color:#fff;border:none;border-radius:6px;margin-top:0.5rem;
`;
const CancelBtn = styled.button`
  padding:0.6rem 1.2rem;background:#ffae00;color:#fff;border:none;border-radius:6px;
  margin-top:0.5rem;margin-left:0.5rem;cursor:pointer;
  &:hover{background:#ff9100;}
`;

/* ---------- component ---------- */
export default function AccountPage() {
  const [tab, setTab] = useState("profile");
  const [user, setUser] = useState(null);
  const [editUser, setEditUser] = useState({
    email:"", username:"", password:"", firstName:"", lastName:"", age:""
  });

  const [bookings, setBookings] = useState([]);
  const [reviews, setReviews] = useState({});
  const [newReview, setNewReview] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const cfg = () => ({ headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` } });
  const todayISO = new Date().toISOString().split("T")[0];

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("No authentication token – please log in.");
      setLoading(false);
      return;
    }

    axios.get("/api/users/me", cfg())
      .then(r => {
        setUser(r.data);
        setEditUser({
          email: r.data.email,
          username: r.data.username,
          password: "Current password not shown (enter new password to change)",
          firstName: r.data.firstName,
          lastName: r.data.lastName,
          age: r.data.age?.toString() ?? ""
        });
      })
      .catch(e => setError("Failed to load profile: " + e.message));

    axios.get("/api/bookings/me", cfg())
      .then(r => {
        setBookings(r.data);
        r.data.forEach(bk => {
          axios.get(`/api/reviews/booking/${bk.id}`, cfg())
            .then(res => setReviews(prev => ({ ...prev, [bk.id]: res.data })))
            .catch(() => {});
        });
      })
      .catch(e => setError("Failed to load bookings: " + e.message))
      .finally(() => setLoading(false));
  }, []);

  const handleSave = () => {
    if (!localStorage.getItem("jwt")) return;
    const payload = {
      email: editUser.email,
      username: editUser.username,
      firstName: editUser.firstName,
      lastName: editUser.lastName,
      age: editUser.age ? parseInt(editUser.age, 10) : undefined
    };
    if (editUser.password.trim() &&
        editUser.password !== "Current password not shown (enter new password to change)") {
      payload.password = editUser.password;
    }

    axios.patch("/api/users/me", payload, cfg())
      .then(r => { setUser(r.data); alert("Profile updated!"); })
      .catch(e => setError("Update failed: " + e.message));
  };

  const handleDelete = () => {
    if (!window.confirm("Delete account permanently?")) return;
    axios.delete("/api/users/me", cfg())
      .then(() => {
        localStorage.removeItem("jwt");
        window.location.href = "/";
      })
      .catch(e => setError("Delete failed: " + e.message));
  };

  const handleReviewSubmit = id => {
    const text = newReview[id] ?? "";
    if (!text.trim()) return;

    axios.post("/api/reviews", {
      bookingId: id, comment: text, rating: 5
    }, cfg())
    .then(r => {
      setReviews(prev => ({ ...prev, [id]: [r.data, ...(prev[id] || [])] }));
      setNewReview(prev => ({ ...prev, [id]: "" }));
    })
    .catch(e => setError("Review failed: " + e.message));
  };

  const handleCancel = id => {
    if (!window.confirm("Cancel this booking?")) return;
    axios.post(`/api/bookings/${id}/cancel`, null, cfg())
      .then(r => {
        setBookings(bks => bks.map(b => b.id === id ? r.data : b));
      })
      .catch(e => setError("Cancellation failed: " + e.message));
  };

  if (loading) return <p>Loading…</p>;

  return (
    <Wrapper>
      <h1>My account</h1>

      <Tabs>
        <Tab active={(tab==="profile").toString()} onClick={() => setTab("profile")}>Profile</Tab>
        <Tab active={(tab==="bookings").toString()} onClick={() => setTab("bookings")}>My bookings</Tab>
      </Tabs>

      {error && <p style={{ color:"#e92365" }}>{error}</p>}

      {/* ---------- PROFILE ---------- */}
      {tab === "profile" && user && (
        <Section>
          {[
            ["Email", "email"], ["Username", "username"],
            ["Password (enter new to change)", "password", "text"],
            ["First name", "firstName"], ["Last name", "lastName"],
            ["Age", "age", "number"]
          ].map(([label, key, type="text"]) => (
            <FormGroup key={key}>
              <Label>{label}</Label>
              <Input type={type} value={editUser[key]}
                     onChange={e => setEditUser({ ...editUser, [key]: e.target.value })} />
            </FormGroup>
          ))}
          <FormGroup>
            <Label>Role (read-only)</Label>
            <Input value={user.role} disabled />
          </FormGroup>
          <SaveButton onClick={handleSave}>Save changes</SaveButton>
          <DeleteButton onClick={handleDelete}>Delete account</DeleteButton>
        </Section>
      )}

      {/* ---------- BOOKINGS ---------- */}
      {tab === "bookings" && (
        <Section>
          <h2>Upcoming & past trips</h2>
          {bookings.length ? bookings.map(bk => {
            const checkInDate = bk.checkIn?.split("T")[0];
const canCancel = bk.status?.toUpperCase() !== "CANCELLED" && checkInDate >= todayISO;

            return (
              <BookingCard key={bk.id}>
                <h3>{bk.listingTitle} ({bk.type})</h3>
                <p>Check-in: {bk.checkIn} | Check-out: {bk.checkOut}</p>
                <p>Guests: {bk.numGuests}</p>
                <p>Status: {bk.status}</p>
                {bk.listingPrice && (
                  <p>Total ≈ €{(bk.listingPrice * (bk.numGuests || 1)).toFixed(0)}</p>
                )}

               {bk.status?.toUpperCase() !== "CANCELLED" ? (
  <>
    <ReviewInput
      placeholder="Add a review…"
      value={newReview[bk.id] || ""}
      onChange={e => setNewReview({ ...newReview, [bk.id]: e.target.value })}
    />
    <SubmitReviewButton onClick={() => handleReviewSubmit(bk.id)}>
      Submit review
    </SubmitReviewButton>
  </>
) : (
  <p style={{ color: "#888", fontStyle: "italic" }}>You can't review a cancelled booking.</p>
)}

                {canCancel && (
                  <CancelBtn onClick={() => handleCancel(bk.id)}>
                    Cancel booking
                  </CancelBtn>
                )}

                {reviews[bk.id]?.length > 0 && (
                  <div>
                    <h4>Reviews</h4>
                    {reviews[bk.id].map(rv => (
                      <p key={rv.id}><strong>{rv.author}</strong>: {rv.comment}</p>
                    ))}
                  </div>
                )}
              </BookingCard>
            );
          }) : <p>You have no bookings yet.</p>}
        </Section>
      )}
    </Wrapper>
  );
}
