import React, { useEffect, useState, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import { DateRange } from "react-date-range";
import { addDays, format, isWithinInterval, parseISO } from "date-fns";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* ---------- destination images (same as HomePage) ---------- */
const destinations = [
  { c: "FR", n: "France", img: "https://images.unsplash.com/photo-1502602898657-3e91760cbb34?w=800&auto=format" },
  { c: "ES", n: "Spain", img: "/destinations/barcelona.jpg" },
  { c: "IT", n: "Italy", img: "/destinations/italy.jpg" },
  { c: "GR", n: "Greece", img: "/destinations/greece.jpg" },
  { c: "JP", n: "Japan", img: "/destinations/japan.jpg" },
  { c: "US", n: "USA", img: "/destinations/usa.jpg" },
  { c: "BR", n: "Brazil", img: "/destinations/brazil.jpg" },
  { c: "TH", n: "Thailand", img: "/destinations/thailand.jpg" },
];

/* ---------- utils ---------- */
const toJsDate = (s) => {
  if (!s) return null;
  try {
    return s.includes("T") ? parseISO(s) : parseISO(s.replace(" ", "T"));
  } catch (e) {
    console.error(`Invalid date format: ${s}`, e);
    return null;
  }
};
const nightsBetween = (a, b) => Math.max(1, Math.round((b - a) / 864e5));
const fmt = (d) => format(d, "d MMM yyyy"); // Added year for clarity

/* ---------- styled bits ---------- */
const fade = keyframes`from{opacity:0}to{opacity:1}`;

const Hero = styled.section`
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0.45), rgba(0, 0, 0, 0.45)), url(${p => p.$img}) center/cover;
  height: 270px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2.6rem;
  font-weight: 800;
  text-transform: uppercase;
  color: #fff;
  text-shadow: 0 3px 12px #000a;
`;

const Filters = styled.div`
  width: 92%;
  max-width: 1200px;
  margin: -48px auto 3rem;
  animation: ${fade} 0.4s;
  background: rgba(255, 255, 255, 0.68);
  backdrop-filter: blur(16px);
  box-shadow: 0 8px 28px rgba(0, 0, 0, 0.14);
  border-radius: 22px;
  padding: 1.6rem 2rem;
  display: grid;
  gap: 1.2rem;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
  label {
    font-weight: 600;
    color: #003b70;
    font-size: 0.9rem;
    display: flex;
    flex-direction: column;
  }
  input,
  select {
    margin-top: 0.45rem;
    padding: 0.5rem 0.75rem;
    font-size: 0.88rem;
    border: 1px solid #b5c9ff;
    border-radius: 12px;
    background: #edf3ff;
  }
  .datePicker {
    grid-column: 1/-1;
  }
  .rdrCalendarWrapper {
    box-shadow: none;
  }
`;

const Grid = styled.section`
  display: grid;
  gap: 2.2rem;
  animation: ${fade} 0.5s;
  max-width: 1440px;
  margin: 0 auto 4rem;
  padding: 0 3rem;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
`;

const Card = styled.div`
  cursor: pointer;
  border-radius: 24px;
  overflow: hidden;
  position: relative;
  box-shadow: 0 8px 22px rgba(0, 0, 0, 0.14);
  transition: 0.3s;
  &:hover {
    transform: translateY(-8px) scale(1.03);
    box-shadow: 0 16px 34px rgba(0, 0, 0, 0.22);
  }
`;

const Thumb = styled.img`
  width: 100%;
  height: 220px;
  object-fit: cover;
`;

const Glass = styled.div`
  position: absolute;
  inset: auto 0 0 0;
  background: rgba(255, 255, 255, 0.78);
  backdrop-filter: blur(15px);
  padding: 1.25rem 1.5rem;
`;

const Title = styled.h3`
  margin: 0 0 0.4rem;
  font-size: 1.15rem;
  color: #003b70;
`;

const Row = styled.p`
  margin: 0.12rem 0;
  font-size: 0.9rem;
  color: #42526e;
  line-height: 1.33;
`;

const Chip = styled.span`
  display: inline-block;
  font-size: 0.68rem;
  font-weight: 700;
  color: #fff;
  padding: 0.15rem 0.6rem;
  border-radius: 999px;
  margin-right: 0.4rem;
  background: ${p => p.bg};
`;

const Price = styled.div`
  font-weight: 700;
  font-size: 1.06rem;
  color: #175dff;
  margin-top: 0.5rem;
`;

const Empty = styled.p`
  grid-column: 1/-1;
  text-align: center;
  font-weight: 600;
  color: #003b70;
`;

const ErrorMessage = styled.p`
  grid-column: 1/-1;
  text-align: center;
  font-weight: 600;
  color: #e92365;
`;

const Loading = styled.p`
  grid-column: 1/-1;
  text-align: center;
  font-weight: 600;
  color: #003b70;
`;

/* ---------- component ---------- */
export default function CountryPage() {
  const { code } = useParams(); // e.g., "FR"
  const navigate = useNavigate();

  /* calendar default → tomorrow .. +7d */
  const [range, setRange] = useState([{
    key: "selection",
    color: "#175dff",
    startDate: addDays(new Date(), 1),
    endDate: addDays(new Date(), 8),
  }]);
  const [guests, setGuests] = useState(2);
  const [stars, setStars] = useState("ANY");
  const [hotels, setHotels] = useState([]);
  const [flights, setFlights] = useState([]);
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  /* load data */
  useEffect(() => {
    (async () => {
      setLoading(true);
      setError(null);
      try {
        const cfg = { headers: { Authorization: `Bearer ${localStorage.getItem("jwt")}` } };
        const [h, f, e] = await Promise.all([
          axios.get("/api/listings/hotels", cfg).catch(e => ({ data: [] })),
          axios.get("/api/listings/flights", cfg).catch(e => ({ data: [] })),
          axios.get("/api/listings/events", cfg).catch(e => ({ data: [] })),
        ]);

        /* convert date strings to Date objects and filter invalid dates */
        const validHotels = h.data
          .map(o => ({
            ...o,
            pricePerNight: o.price,
            availableFrom: toJsDate(o.availableFrom),
            availableTo: toJsDate(o.availableTo),
          }))
          .filter(o => o.availableFrom && o.availableTo);
        const validFlights = f.data
          .map(o => ({
            ...o,
            departureTime: toJsDate(o.departureTime),
            arrivalTime: toJsDate(o.arrivalTime),
          }))
          .filter(o => o.departureTime && o.arrivalTime);
        const validEvents = e.data
          .map(o => ({
            ...o,
            eventDate: toJsDate(o.eventDate),
          }))
          .filter(o => o.eventDate);

        console.log("Filtered Hotels:", validHotels);
        console.log("Filtered Flights:", validFlights);
        console.log("Filtered Events:", validEvents);

        setHotels(validHotels);
        setFlights(validFlights);
        setEvents(validEvents);
      } catch (err) {
        setError("Failed to load packages. Please try again later.");
        console.error("API Error:", err);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  /* compute bundles */
  const bundles = useMemo(() => {
    const [{ startDate: from, endDate: to }] = range;
    if (!from || !to) {
      console.log("Invalid date range:", { from, to });
      return [];
    }

    const nights = nightsBetween(from, to);

    /* filter hotels: available for at least one day in the range */
    const hs = hotels.filter(h => {
      const matchCountry = h.country === code;
      const matchStars = stars === "ANY" || (h.starRating || 0) >= +stars;
      const matchGuests = h.totalRooms >= guests;
      const matchDates = isWithinInterval(h.availableFrom, { start: from, end: to }) ||
                         isWithinInterval(h.availableTo, { start: from, end: to }) ||
                         (h.availableFrom <= from && h.availableTo >= to);
      return matchCountry && matchStars && matchGuests && matchDates;
    });

    /* filter flights: departure and arrival within range */
    const fs = flights.filter(f => {
      const matchCountry = f.country === code;
      const matchGuests = f.seatCapacity >= guests;
      const matchDates = isWithinInterval(f.departureTime, { start: from, end: to }) &&
                         isWithinInterval(f.arrivalTime, { start: from, end: to });
      return matchCountry && matchGuests && matchDates;
    });

    /* filter events: event date within range */
    const es = events.filter(ev => {
      const matchCountry = ev.country === code;
      const matchGuests = ev.ticketCapacity >= guests;
      const matchDate = isWithinInterval(ev.eventDate, { start: from, end: to });
      return matchCountry && matchGuests && matchDate;
    });

    console.log("Filtered for", code, ":", { hotels: hs, flights: fs, events: es });

    /* create bundles: include partial matches (at least hotel + one other) */
    const out = [];
    hs.forEach(h => {
      // Hotel only (if no flights or events)
      out.push({
        id: `h${h.id}`,
        hotel: h,
        flight: null,
        event: null,
        nights,
        window: `${fmt(h.availableFrom)} – ${fmt(h.availableTo)}`,
        total: nights * h.pricePerNight,
        thumb: h.photoUrl ?? "https://via.placeholder.com/400x250",
      });

      // Hotel + Flight
      fs.forEach(f => {
        out.push({
          id: `h${h.id}-f${f.id}`,
          hotel: h,
          flight: f,
          event: null,
          nights,
          window: `${fmt(f.departureTime)} – ${fmt(f.arrivalTime)}`,
          total: nights * h.pricePerNight + f.price,
          thumb: h.photoUrl ?? f.photoUrl ?? "https://via.placeholder.com/400x250",
        });
      });

      // Hotel + Event
      es.forEach(ev => {
        out.push({
          id: `h${h.id}-e${ev.id}`,
          hotel: h,
          flight: null,
          event: ev,
          nights,
          window: `${fmt(h.availableFrom)} – ${fmt(h.availableTo)}`,
          total: nights * h.pricePerNight + ev.price,
          thumb: h.photoUrl ?? ev.photoUrl ?? "https://via.placeholder.com/400x250",
        });
      });

      // Hotel + Flight + Event
      fs.forEach(f => es.forEach(ev => {
        out.push({
          id: `h${h.id}-f${f.id}-e${ev.id}`,
          hotel: h,
          flight: f,
          event: ev,
          nights,
          window: `${fmt(f.departureTime)} – ${fmt(f.arrivalTime)}`,
          total: nights * h.pricePerNight + f.price + ev.price,
          thumb: h.photoUrl ?? f.photoUrl ?? ev.photoUrl ?? "https://via.placeholder.com/400x250",
        });
      }));
    });

    return out.sort((a, b) => a.total - b.total).slice(0, 40);
  }, [code, range, guests, stars, hotels, flights, events]);

  /* hero image (same as HomePage) */
  const heroImg = destinations.find(d => d.c === code)?.img || `https://source.unsplash.com/1600x600/?${code},landmark`;

  /* ---------- render ---------- */
  return (
    <>
      <Hero $img={heroImg}>{code}</Hero>

      <Filters>
        <label className="datePicker">
          Dates
          <DateRange
            ranges={range}
            onChange={r => setRange([{ ...r.selection, color: "#175dff" }])}
            moveRangeOnFirstSelection={false}
            rangeColors={["#175dff"]}
          />
        </label>

        <label>
          Travellers
          <input
            type="number"
            min={1}
            max={10}
            value={guests}
            onChange={e => setGuests(Math.max(1, Math.min(10, +e.target.value || 1)))}
          />
        </label>

        <label>
          Hotel rating
          <select value={stars} onChange={e => setStars(e.target.value)}>
            <option value="ANY">Any</option>
            <option value="3">3★ +</option>
            <option value="4">4★ +</option>
            <option value="5">5★</option>
          </select>
        </label>
      </Filters>

      <Grid>
        {loading ? (
          <Loading>Loading packages...</Loading>
        ) : error ? (
          <ErrorMessage>{error}</ErrorMessage>
        ) : bundles.length ? (
          bundles.map(b => (
            <Card key={b.id} onClick={() => navigate(`/packages/${b.id}`, { state: b })}>
              <Thumb src={b.thumb} alt={b.hotel.title} />
              <Glass>
                <Title>{b.hotel.title}</Title>
                <Row>
                  🗓 {b.window}<br />
                  {b.nights} nights · {b.hotel.city}<br />
                  {b.flight && <Chip bg="#175dff">✈ {b.flight.airline}</Chip>}
                  {b.event && (
                    <Chip bg="#e92365">
                      🎫 {b.event.title.length > 18 ? b.event.title.slice(0, 18) + "…" : b.event.title}
                    </Chip>
                  )}
                </Row>
                <Price>€ {b.total.toFixed(0)}</Price>
              </Glass>
            </Card>
          ))
        ) : (
          <Empty>No bundles match your filters. Try adjusting dates or filters.</Empty>
        )}
      </Grid>
    </>
  );
}
