/* src/pages/CountryPage.jsx */
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useNavigate }  from "react-router-dom";
import styled, { keyframes }       from "styled-components";
import axios                       from "axios";
import { DateRange }               from "react-date-range";
import { addDays }                 from "date-fns";

import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ styles â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const fade = keyframes`from{opacity:0}to{opacity:1}`;

const Hero = styled.section`
  /* dark overlay so white text pops */
  background:
    linear-gradient(to bottom,rgba(0,0,0,.35),rgba(0,0,0,.35)),
    url(${p => p.$img}) center/cover;

  height: 260px;
  display: flex; align-items: center; justify-content: center;
  color: #fff; font-size: 2.6rem; font-weight: 800;
  text-shadow: 0 2px 8px #0006;
`;

const Filters = styled.div`
  width: 95%; max-width: 1080px; margin: -40px auto 2.5rem;
  padding: 1.25rem 1.75rem; border-radius: 20px;
  backdrop-filter: blur(16px);
  background: rgba(255, 255, 255, .55);
  box-shadow: 0 8px 28px rgba(0,0,0,.12);
  animation: ${fade} .5s ease-out;
`;

const Grid = styled.section`
  display: grid; gap: 2rem;
  max-width: 1400px; margin: 0 auto 4rem; padding: 0 3rem;
  grid-template-columns: repeat(auto-fit,minmax(300px,1fr));
  animation: ${fade} .6s ease-out;
`;

const Card = styled.div`
  position: relative; border-radius: 22px; overflow: hidden; cursor: pointer;
  transition: .35s; box-shadow: 0 6px 22px rgba(0,0,0,.14);

  &:hover{
    transform: translateY(-8px) scale(1.03);
    box-shadow: 0 14px 32px rgba(0,0,0,.20);
  }
`;

const Thumb  = styled.img`
  width: 100%; height: 220px; object-fit: cover;
`;

const Glass  = styled.div`
  position: absolute; inset: auto 0 0 0; padding: 1rem 1.25rem;
  background: rgba(255,255,255,.72); backdrop-filter: blur(14px);
`;

const Title  = styled.h3`
  margin: 0 0 .4rem; font-size: 1.15rem; color: #003b70;
`;

const Chip   = styled.span`
  display: inline-block; font-size: .68rem; font-weight: 700;
  padding: .15rem .55rem; border-radius: 999px; background: ${p=>p.bg};
  color: #fff; margin-right: .35rem;
`;

const Row    = styled.p`
  margin: .1rem 0; font-size: .9rem; color: #42526e;
`;

const Price  = styled.div`
  font-weight: 700; font-size: 1.08rem; color: #175dff; margin-top: .45rem;
`;

const Empty  = styled.p`
  grid-column: 1 / -1; text-align: center; font-weight: 600; color: #003b70;
`;

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ helpers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const diffDays = (a,b)=>Math.max(1,Math.round((b-a)/864e5));

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ component â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
export default function CountryPage() {
  const { code } = useParams();     // e.g. "FR"
  const nav      = useNavigate();

  /* default calendar: tomorrow â†’ +7 days */
  const [range,setRange] = useState([
    { key:"selection",
      startDate: addDays(new Date(),1),
      endDate  : addDays(new Date(),8) }
  ]);

  /* raw listings */
  const [hotels ,setHotels ] = useState([]);
  const [flights,setFlights] = useState([]);
  const [events ,setEvents ] = useState([]);

  /* one-time fetch */
  useEffect(()=>{
    (async()=>{
      const cfg = { headers:{Authorization:`Bearer ${localStorage.getItem("jwt")}`} };

      const [h,f,e] = await Promise.all([
        axios.get("/api/listings/hotels",  cfg),
        axios.get("/api/listings/flights", cfg),
        axios.get("/api/listings/events",  cfg)
      ]);

      setHotels (h.data.map(o=>({...o, pricePerNight:o.price})));
      setFlights(f.data);
      setEvents (e.data);
    })();
  },[]);

  /* derive â€œbundlesâ€ */
  const bundles = useMemo(()=>{
    if(!range[0]) return [];

    const { startDate:from, endDate:to } = range[0];
    const nights = diffDays(from,to);

    const hs = hotels .filter(h =>
        h.country===code &&
        new Date(h.availableFrom)<=from &&
        new Date(h.availableTo)>=to );

    const fs = flights.filter(f =>
        f.country===code &&
        new Date(f.departureTime)>=from &&
        new Date(f.arrivalTime)<=to );

    const es = events .filter(ev=>
        ev.country===code &&
        new Date(ev.eventDate)>=from &&
        new Date(ev.eventDate)<=to );

    const out=[];
    hs.forEach(h=>{
      fs.forEach(f=>{
        es.forEach(ev=>{
          out.push({
            id   :`${h.id}-${f.id}-${ev.id}`,
            hotel:h, flight:f, event:ev, nights,
            total:nights*h.pricePerNight + f.price + ev.price,
            thumb:h.photoUrl ?? ev.photoUrl ?? f.photoUrl
          });
        });
      });
    });

    return out.sort((a,b)=>a.total-b.total).slice(0,24);
  },[code,range,hotels,flights,events]);

  /* hero image */
  const heroImg = `https://source.unsplash.com/1600x600/?${code},landmark`;

  /* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ JSX â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  return (
    <>
      <Hero $img={heroImg}>{code}</Hero>

      <Filters>
        <strong style={{color:"#003b70",marginRight:"1rem"}}>Dates</strong>
        <DateRange
          ranges={range}
          onChange={r=> setRange([ r.selection ])}
          moveRangeOnFirstSelection={false}
          rangeColors={["#175dff"]}
          editableDateInputs
        />
      </Filters>

      <Grid>
        {bundles.length ? bundles.map(b=>(
          <Card key={b.id} onClick={()=>nav(`/packages/${b.id}`,{state:b})}>
            <Thumb src={b.thumb || "https://via.placeholder.com/400x250"} />
            <Glass>
              <Title>{b.hotel.title}</Title>
              <Row>
                {b.nights} nights Â· {b.hotel.city}<br/>
                <Chip bg="#175dff">âœˆ {b.flight.airline}</Chip>
                <Chip bg="#e92365">
                  ðŸŽ« {b.event.title.length>18 ? b.event.title.slice(0,18)+"â€¦" : b.event.title}
                </Chip>
              </Row>
              <Price>â‚¬ {b.total.toFixed(0)}</Price>
            </Glass>
          </Card>
        )) : <Empty>No matching bundles â€“ try another date window.</Empty>}
      </Grid>
    </>
  );
}
