// src/pages/AdminEditListingPage.jsx
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import Header from "../components/Header";

const fade = keyframes`from{opacity:0}to{opacity:1}`;
const accent = "#175dff";

const FormWrapper = styled.div`
  padding: 2rem;
  animation: ${fade} .5s ease-out;
`;
const Form = styled.form`
  max-width: 600px;
  margin: 2rem auto;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;
const Label = styled.label`
  font-weight: 600;
`;
const Input = styled.input`
  padding: .6rem;
  border: 1px solid #ccc;
  border-radius: 4px;
`;
const ButtonRow = styled.div`
  display: flex;
  gap: 1rem;
`;
const ButtonPrimary = styled.button`
  padding: .6rem 1.2rem;
  background: ${accent};
  color: #fff;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;
const ButtonSecondary = styled(ButtonPrimary)`
  background: #aaa;
`;

export default function AdminEditListingPage() {
  const { category, id } = useParams();
  const isNew = id === 'new';
  const [form, setForm] = useState({ title:'', imageUrl:'' });
  const navigate = useNavigate();

  useEffect(() => {
    if (!isNew) {
      const token = localStorage.getItem("jwt");
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      axios.get(`/api/listings/${category}/${id}`)
        .then(res => setForm(res.data))
        .catch(console.error);
    }
  }, [category, id, isNew]);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    const token = localStorage.getItem("jwt");
    const method = isNew ? 'post' : 'put';
    axios({
      method,
      url: `/api/listings/${category}${isNew?'':`/${id}`}`,
      data: form,
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => navigate(`/admin/${category}`))
    .catch(console.error);
  };

  return (
    <div>
      <Header />
      <FormWrapper>
        <h2>{isNew ? 'Add New' : 'Edit'} {category}</h2>
        <Form onSubmit={handleSubmit}>
          <div>
            <Label>Title</Label>
            <Input name="title" value={form.title||''} onChange={handleChange} required />
          </div>
          {category==='flights' && (
            <>
              <div><Label>Departure</Label><Input name="departure" value={form.departure||''} onChange={handleChange} required/></div>
              <div><Label>Arrival</Label><Input name="arrival" value={form.arrival||''} onChange={handleChange} required/></div>
              <div><Label>Seat Capacity</Label><Input type="number" name="seatCapacity" value={form.seatCapacity||0} onChange={handleChange} required/></div>
            </>
          )}
          {category==='hotels' && (
            <>
              <div><Label>City</Label><Input name="city" value={form.city||''} onChange={handleChange} required/></div>
              <div><Label>Total Rooms</Label><Input type="number" name="totalRooms" value={form.totalRooms||0} onChange={handleChange} required/></div>
            </>
          )}
          {category==='events' && (
            <>
              <div><Label>Event Date</Label><Input type="date" name="eventDate" value={form.eventDate||''} onChange={handleChange} required/></div>
              <div><Label>Ticket Capacity</Label><Input type="number" name="ticketCapacity" value={form.ticketCapacity||0} onChange={handleChange} required/></div>
            </>
          )}
          <div><Label>Image URL</Label><Input name="imageUrl" value={form.imageUrl||''} onChange={handleChange} /></div>
          <ButtonRow>
            <ButtonPrimary type="submit">Save</ButtonPrimary>
            <ButtonSecondary type="button" onClick={()=>navigate(`/admin/${category}`)}>Cancel</ButtonSecondary>
          </ButtonRow>
        </Form>
      </FormWrapper>
    </div>
  );
}
