import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import Header from "../components/Header"; // Reusable header

/* --- palette & animations --- */
const pageBlue = "#d8edff";
const cardBlue = "#b4d0ff";
const accent1 = "#175dff";
const textDark = "#003b70";
const fadeIn = keyframes`from{opacity:0;transform:translateY(15px)}
                         to{opacity:1;transform:translateY(0)}`;
const zoom = keyframes`from{transform:scale(1)}
                       to{transform:scale(1.08)}`;

/* --- layout --- */
const Wrapper = styled.div`
  min-height: 100vh;
  background: ${pageBlue};
  display: flex;
  flex-direction: column;
  font-family: "Segoe UI", Tahoma, Verdana, sans-serif;
`;

/* hero */
const Hero = styled.section`
  background: url("https://images.unsplash.com/photo-1507525428034-b723cf961d3e?w=1600&auto=format")
              center/cover fixed;
  color: #fff;
  text-align: center;
  padding: 4.5rem 1rem 6rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  animation: ${fadeIn} .7s ease-out;
`;
const HeroTitle = styled.h1`
  font-size: 3rem;
  font-weight: 800;
  margin: 0 0 1rem;
`;

/* tabs */
const Tabs = styled.div`
  display: flex;
  gap: 2rem;
  border-bottom: 2px solid #e0e0e0;
  max-width: 1400px;
  margin: -4rem auto 0;
  padding: 0 2.5rem;
  animation: ${fadeIn} .8s ease-out;
`;
const Tab = styled.button`
  background: none;
  border: none;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  padding: 0.6rem 0;
  color: ${p => p.isActive ? accent1 : "#777"};
  border-bottom: 3px solid ${p => p.isActive ? accent1 : "transparent"};
`;

/* grid */
const Grid = styled.section`
  width: 100%;
  max-width: 1400px;
  margin: 2rem auto;
  display: grid;
  gap: 2rem;
  padding: 0 2.5rem 4rem;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  animation: ${fadeIn} .8s ease-out;
`;
const Card = styled.div`
  position: relative;
  border-radius: 18px;
  overflow: hidden;
  height: 260px;
  cursor: pointer;
  box-shadow: 0 8px 18px rgba(0,0,0,.15);
  background: ${cardBlue};
  transition: transform .3s;
  &:hover { transform: translateY(-6px); }
  &:hover img { animation: ${zoom} 5s ease-out forwards; }
`;
const Img = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: ${p => p.src ? "block" : "none"};
`;
const Mask = styled.div`
  position: absolute;
  inset: 0;
  background: linear-gradient(to bottom, transparent 42%, rgba(0,0,0,.65));
`;
const Label = styled.h3`
  position: absolute;
  bottom: 1.2rem;
  left: 1.4rem;
  margin: 0;
  color: #fff;
  font-size: 1.5rem;
  font-weight: 600;
`;
const CardContent = styled.div`
  padding: 1rem;
  color: ${textDark};
`;
const CardTitle = styled.h3`
  margin: 0 0 0.5rem;
  font-size: 1.2rem;
`;
const CardDetail = styled.p`
  margin: 0.25rem 0;
  font-size: 0.95rem;
`;
const Button = styled.button`
  padding: 0.6rem 1.2rem;
  background: ${accent1};
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin: 0.5rem 0.5rem 0 0;
  &:hover { background: #004bff; }
`;

/* form modal */
const ModalBG = styled.div`
  position: fixed;
  inset: 0;
  background: rgba(0,0,0,.5);
  display: ${p => p.open ? "flex" : "none"};
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;
const ModalContent = styled.div`
  background: #fff;
  padding: 2rem;
  border-radius: 10px;
  max-width: 500px;
  width: 90%;
  position: relative;
`;
const CloseBtn = styled.button`
  position: absolute;
  top: 8px;
  right: 8px;
  background: none;
  border: none;
  font-size: 1.2rem;
  cursor: pointer;
`;
const FormGroup = styled.div`
  margin-bottom: 1rem;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.6rem;
  border: 1px solid #d1e0ff;
  border-radius: 6px;
`;

export default function AdminPage() {
  const [tab, setTab] = useState("flights");
  const [flights, setFlights] = useState([]);
  const [hotels, setHotels] = useState([]);
  const [events, setEvents] = useState([]);
  const [users, setUsers] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [formData, setFormData] = useState({});
  const [editingItem, setEditingItem] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("No authentication token found. Redirecting to login...");
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    Promise.all([
      axios.get("/api/listings/flights"),
      axios.get("/api/listings/hotels"),
      axios.get("/api/listings/events"),
      axios.get("/api/users"),
      axios.get("/api/admin/bookings"),
      axios.get("/api/reviews/listing/1"), // Example listing ID
    ])
      .then(([flightsRes, hotelsRes, eventsRes, usersRes, bookingsRes, reviewsRes]) => {
        setFlights(flightsRes.data);
        setHotels(hotelsRes.data);
        setEvents(eventsRes.data);
        setUsers(usersRes.data);
        setBookings(bookingsRes.data);
        setReviews(reviewsRes.data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error loading data:", err);
        setError(`Failed to load data: ${err.response?.status === 401 ? "Unauthorized - please log in again" : err.message}`);
        setLoading(false);
        if (err.response?.status === 401) {
          setTimeout(() => navigate("/login"), 2000);
        }
      });
  }, [navigate]);

  const handleAddOrUpdate = async () => {
    const token = localStorage.getItem("jwt");
    const endpoint = editingItem ? `/api/listings/${tab}/${editingItem.id}` : `/api/listings/${tab}`;
    const method = editingItem ? "PUT" : "POST";
    try {
      const res = await axios({
        method,
        url: endpoint,
        data: formData,
        headers: { Authorization: `Bearer ${token}` },
      });
      if (tab === "flights") {
        setFlights(editingItem ? flights.map(f => f.id === editingItem.id ? res.data : f) : [...flights, res.data]);
      } else if (tab === "hotels") {
        setHotels(editingItem ? hotels.map(h => h.id === editingItem.id ? res.data : h) : [...hotels, res.data]);
      } else if (tab === "events") {
        setEvents(editingItem ? events.map(e => e.id === editingItem.id ? res.data : e) : [...events, res.data]);
      }
      setModalOpen(false);
      setFormData({});
      setEditingItem(null);
    } catch (err) {
      setError(`Failed to ${editingItem ? "update" : "add"} item: ${err.message}`);
    }
  };

  const handleDelete = async (id, type) => {
    const token = localStorage.getItem("jwt");
    try {
      await axios.delete(`/api/listings/${type}/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      if (type === "flights") {
        setFlights(flights.filter(f => f.id !== id));
      } else if (type === "hotels") {
        setHotels(hotels.filter(h => h.id !== id));
      } else if (type === "events") {
        setEvents(events.filter(e => e.id !== id));
      }
    } catch (err) {
      setError(`Failed to delete item: ${err.message}`);
    }
  };

  const handleDeleteUser = async id => {
    const token = localStorage.getItem("jwt");
    try {
      await axios.delete(`/api/users/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      setUsers(users.filter(u => u.id !== id));
    } catch (err) {
      setError(`Failed to delete user: ${err.message}`);
    }
  };

  const handleDeleteBooking = async id => {
    const token = localStorage.getItem("jwt");
    try {
      await axios.delete(`/api/admin/bookings/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      setBookings(bookings.filter(b => b.id !== id));
    } catch (err) {
      setError(`Failed to delete booking: ${err.message}`);
    }
  };

  const handleModerateReview = async (reviewId, approved, remarks) => {
    const token = localStorage.getItem("jwt");
    try {
      await axios.post(`/api/reviews/${reviewId}/moderate`, null, {
        headers: { Authorization: `Bearer ${token}` },
        params: { approved, remarks },
      });
      setReviews(reviews.map(r => r.id === reviewId ? { ...r, approved, remarks } : r));
    } catch (err) {
      setError(`Failed to moderate review: ${err.message}`);
    }
  };

  const openForm = (item = null, type) => {
    setEditingItem(item ? { ...item, type } : null);
    setFormData(item || {});
    setModalOpen(true);
  };

  if (loading) return <Wrapper><Header /><p>Loading...</p></Wrapper>;

  return (
    <Wrapper>
      <Header />
      <Hero>
        <HeroTitle>Admin Panel</HeroTitle>
        <p style={{ fontSize: "1.15rem", maxWidth: 640 }}>
          Manage flights, hotels, events, users, bookings, and reviews.
        </p>
      </Hero>
      <Tabs>
        <Tab isActive={tab === "flights"} onClick={() => setTab("flights")}>Flights</Tab>
        <Tab isActive={tab === "hotels"} onClick={() => setTab("hotels")}>Hotels</Tab>
        <Tab isActive={tab === "events"} onClick={() => setTab("events")}>Events</Tab>
        <Tab isActive={tab === "users"} onClick={() => setTab("users")}>Users</Tab>
        <Tab isActive={tab === "bookings"} onClick={() => setTab("bookings")}>Bookings</Tab>
        <Tab isActive={tab === "reviews"} onClick={() => setTab("reviews")}>Reviews</Tab>
      </Tabs>
      <Grid>
        {tab === "flights" &&
          flights.map(flight => (
            <Card key={flight.id}>
              {flight.imageUrl && <Img src={flight.imageUrl} alt={flight.title} />}
              <Mask />
              <Label>{flight.title}</Label>
              <CardContent>
                <CardTitle>{flight.departure} â†’ {flight.arrival}</CardTitle>
                <CardDetail>Seats: {flight.seatCapacity}</CardDetail>
                <Button onClick={() => openForm(flight, "flights")}>Edit</Button>
                <Button onClick={() => handleDelete(flight.id, "flights")}>Delete</Button>
              </CardContent>
            </Card>
          ))}
        {tab === "hotels" &&
          hotels.map(hotel => (
            <Card key={hotel.id}>
              {hotel.imageUrl && <Img src={hotel.imageUrl} alt={hotel.title} />}
              <Mask />
              <Label>{hotel.title}</Label>
              <CardContent>
                <CardTitle>{hotel.city}</CardTitle>
                <CardDetail>Rooms: {hotel.totalRooms}</CardDetail>
                <Button onClick={() => openForm(hotel, "hotels")}>Edit</Button>
                <Button onClick={() => handleDelete(hotel.id, "hotels")}>Delete</Button>
              </CardContent>
            </Card>
          ))}
        {tab === "events" &&
          events.map(event => (
            <Card key={event.id}>
              {event.imageUrl && <Img src={event.imageUrl} alt={event.title} />}
              <Mask />
              <Label>{event.title}</Label>
              <CardContent>
                <CardTitle>{new Date(event.eventDate).toLocaleDateString()}</CardTitle>
                <CardDetail>Tickets: {event.ticketCapacity}</CardDetail>
                <Button onClick={() => openForm(event, "events")}>Edit</Button>
                <Button onClick={() => handleDelete(event.id, "events")}>Delete</Button>
              </CardContent>
            </Card>
          ))}
        {tab === "users" &&
          users.map(user => (
            <Card key={user.id}>
              <CardContent>
                <CardTitle>{user.username}</CardTitle>
                <CardDetail>Email: {user.email}</CardDetail>
                <CardDetail>Role: {user.role}</CardDetail>
                <Button onClick={() => handleDeleteUser(user.id)}>Delete</Button>
              </CardContent>
            </Card>
          ))}
        {tab === "bookings" &&
          bookings.map(booking => (
            <Card key={booking.id}>
              <CardContent>
                <CardTitle>Booking #{booking.id}</CardTitle>
                <CardDetail>User ID: {booking.userId}</CardDetail>
                <CardDetail>Status: {booking.status}</CardDetail>
                <Button onClick={() => handleDeleteBooking(booking.id)}>Delete</Button>
              </CardContent>
            </Card>
          ))}
        {tab === "reviews" &&
          reviews.map(review => (
            <Card key={review.id}>
              <CardContent>
                <CardTitle>Review #{review.id}</CardTitle>
                <CardDetail>Comment: {review.comment}</CardDetail>
                <CardDetail>Status: {review.approved ? "Approved" : "Pending"}</CardDetail>
                <Button onClick={() => handleModerateReview(review.id, true, "Approved by admin")}>
                  Approve
                </Button>
                <Button onClick={() => handleModerateReview(review.id, false, "Rejected by admin")}>
                  Reject
                </Button>
              </CardContent>
            </Card>
          ))}
      </Grid>
      <Button
        style={{ margin: "0 auto 2rem", display: "block" }}
        onClick={() => openForm(null, tab)}
      >
        Add New {tab.slice(0, -1)}
      </Button>
      <ModalBG open={modalOpen}>
        <ModalContent>
          <CloseBtn onClick={() => setModalOpen(false)}>Ã—</CloseBtn>
          <h2>{editingItem ? "Edit" : "Add"} {tab.slice(0, -1)}</h2>
          <FormGroup>
            <Label>Title</Label>
            <Input
              value={formData.title || ""}
              onChange={e => setFormData({ ...formData, title: e.target.value })}
            />
          </FormGroup>
          {tab === "flights" && (
            <>
              <FormGroup>
                <Label>Departure</Label>
                <Input
                  value={formData.departure || ""}
                  onChange={e => setFormData({ ...formData, departure: e.target.value })}
                />
              </FormGroup>
              <FormGroup>
                <Label>Arrival</Label>
                <Input
                  value={formData.arrival || ""}
                  onChange={e => setFormData({ ...formData, arrival: e.target.value })}
                />
              </FormGroup>
              <FormGroup>
                <Label>Seat Capacity</Label>
                <Input
                  type="number"
                  value={formData.seatCapacity || ""}
                  onChange={e => setFormData({ ...formData, seatCapacity: +e.target.value })}
                />
              </FormGroup>
            </>
          )}
          {tab === "hotels" && (
            <>
              <FormGroup>
                <Label>City</Label>
                <Input
                  value={formData.city || ""}
                  onChange={e => setFormData({ ...formData, city: e.target.value })}
                />
              </FormGroup>
              <FormGroup>
                <Label>Total Rooms</Label>
                <Input
                  type="number"
                  value={formData.totalRooms || ""}
                  onChange={e => setFormData({ ...formData, totalRooms: +e.target.value })}
                />
              </FormGroup>
            </>
          )}
          {tab === "events" && (
            <>
              <FormGroup>
                <Label>Event Date</Label>
                <Input
                  type="date"
                  value={formData.eventDate || ""}
                  onChange={e => setFormData({ ...formData, eventDate: e.target.value })}
                />
              </FormGroup>
              <FormGroup>
                <Label>Ticket Capacity</Label>
                <Input
                  type="number"
                  value={formData.ticketCapacity || ""}
                  onChange={e => setFormData({ ...formData, ticketCapacity: +e.target.value })}
                />
              </FormGroup>
            </>
          )}
          <FormGroup>
            <Label>Image URL</Label>
            <Input
              value={formData.imageUrl || ""}
              onChange={e => setFormData({ ...formData, imageUrl: e.target.value })}
            />
          </FormGroup>
          <Button onClick={handleAddOrUpdate}>{editingItem ? "Update" : "Add"}</Button>
        </ModalContent>
      </ModalBG>
      {error && (
        <p style={{ color: "#e92365", textAlign: "center", margin: "2rem" }}>{error}</p>
      )}
    </Wrapper>
  );
}
