import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import Header from "../components/Header";

const fadeIn = keyframes`
  from { opacity:0; transform:translateY(10px) }
  to   { opacity:1; transform:translateY(0) }
`;
const Wrapper = styled.div` background:#f0f4f8; min-height:100vh; `;
const Content = styled.div`
  max-width:1000px; margin:2rem auto; animation:${fadeIn}.5s ease-out;
`;
const TitleBar = styled.div`
  display:flex; justify-content:space-between; margin-bottom:1rem;
`;
const Title = styled.h2`margin:0;`;
const AddButton = styled(Link)`
  padding:0.6rem 1rem; background:#175dff; color:#fff; border-radius:5px;
  text-decoration:none;&:hover{background:#004bff;}
`;
const Table = styled.table`
  width:100%; border-collapse:collapse;
  background:#fff; box-shadow:0 2px 6px rgba(0,0,0,0.1);
`;
const Th = styled.th`
  padding:0.75rem; background:#e1eaff; text-align:left;
`;
const Td = styled.td`
  padding:0.75rem; border-top:1px solid #eee;
`;
const Action = styled.span`
  color:#175dff; cursor:pointer; margin-right:0.5rem;
  &:hover{text-decoration:underline;}
`;

export default function AdminListingsPage() {
  const { category } = useParams();
  const [items, setItems] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) return navigate("/login");
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    const url =
      category === "users"
        ? "/api/users"
        : `/api/listings/${category}`;

    axios.get(url)
      .then(({ data }) => setItems(data))
      .catch(() => setError("Failed to load"));
  }, [category, navigate]);

  const singular = category.slice(0, -1).toUpperCase();

  return (
    <Wrapper>
      <Header/>
      <Content>
        <TitleBar>
          <Title>Manage {singular}</Title>
          {category!=="users" && (
            <AddButton to={`/admin/${category}/new`}>
              + Add New
            </AddButton>
          )}
        </TitleBar>
        {error && <p style={{color:"red"}}>{error}</p>}
        <Table>
          <thead>
            <tr>
              {category==="users" && <>
                <Th>Username</Th><Th>Email</Th><Th>Role</Th><Th>Actions</Th>
              </>}
              {category==="hotels" && <>
                <Th>Title</Th><Th>City</Th><Th>Price</Th><Th>Actions</Th>
              </>}
              {category==="flights" && <>
                <Th>Title</Th><Th>Airline</Th><Th>Price</Th><Th>Actions</Th>
              </>}
              {category==="events" && <>
                <Th>Title</Th><Th>Venue</Th><Th>Date</Th><Th>Actions</Th>
              </>}
            </tr>
          </thead>
          <tbody>
            {items.map(it=>(
              <tr key={it.id}>
                {category==="users" && <>
                  <Td>{it.username}</Td>
                  <Td>{it.email}</Td>
                  <Td>{it.role}</Td>
                  <Td>
                    <Link to={`/admin/users/${it.id}/edit`}>Edit</Link>{" "}
                    <Action onClick={()=>
                      axios.delete(`/api/users/${it.id}`)
                        .then(()=>setItems(items.filter(u=>u.id!==it.id)))
                    }>Delete</Action>
                  </Td>
                </>}
                {category==="hotels" && <>
                  <Td>{it.title}</Td><Td>{it.city}</Td><Td>{it.price}</Td>
                  <Td>
                    <Link to={`/admin/hotels/${it.id}/edit`}>Edit</Link>{" "}
                    <Action onClick={()=>
                      axios.delete(`/api/listings/hotels/${it.id}`)
                        .then(()=>setItems(items.filter(h=>h.id!==it.id)))
                    }>Delete</Action>
                  </Td>
                </>}
                {category==="flights" && <>
                  <Td>{it.title}</Td><Td>{it.airline}</Td><Td>{it.price}</Td>
                  <Td>
                    <Link to={`/admin/flights/${it.id}/edit`}>Edit</Link>{" "}
                    <Action onClick={()=>
                      axios.delete(`/api/listings/flights/${it.id}`)
                        .then(()=>setItems(items.filter(f=>f.id!==it.id)))
                    }>Delete</Action>
                  </Td>
                </>}
                {category==="events" && <>
                  <Td>{it.title}</Td><Td>{it.venue}</Td>
                  <Td>{new Date(it.eventDate).toLocaleString()}</Td>
                  <Td>
                    <Link to={`/admin/events/${it.id}/edit`}>Edit</Link>{" "}
                    <Action onClick={()=>
                      axios.delete(`/api/listings/events/${it.id}`)
                        .then(()=>setItems(items.filter(e=>e.id!==it.id)))
                    }>Delete</Action>
                  </Td>
                </>}
              </tr>
            ))}
          </tbody>
        </Table>
      </Content>
    </Wrapper>
  );
}
