/* ────────────────────────────────────────────────────────────────
   Bookify • CountryPage (error-free)
   ----------------------------------------------------------------
   – works with date-fns v2
   – hero image fallback
   – sensible bundle logic (same-day flight - hotel check-in/out)
   – filters: dates · guests · hotel stars
   ────────────────────────────────────────────────────────────────*/
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import { DateRange } from "react-date-range";
import { addDays, format, isSameDay, isWithinInterval, parseISO } from "date-fns";

import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* ---------- local hero images (served from CRA /public ) ---------- */
const heroPic = c => `${process.env.PUBLIC_URL}/destinations/${c.toLowerCase()}.jpg`;

/* ---------- utils ---------- */
const toJsDate = s => {               // accepts "2025-06-20 12:00:00" or ISO-8601
  if (!s) return null;
  return s.includes("T") ? parseISO(s) : parseISO(s.replace(" ", "T"));
};
const nightsBetween = (a, b) =>
  Math.max(1, Math.round((b - a) / 864e5));
const fmt = d => format(d, "d MMM");

/* ---------- styled bits ---------- */
const fade = keyframes`from{opacity:0}to{opacity:1}`;

const Hero = styled.section`
  background:
     linear-gradient(to bottom,rgba(0,0,0,.45),rgba(0,0,0,.45)),
     url(${p => p.$img}) center/cover;
  height:270px; display:flex; align-items:center; justify-content:center;
  font-size:2.6rem; font-weight:800; text-transform:uppercase;
  color:#fff; text-shadow:0 3px 12px #000a;
`;

const Filters = styled.div`
  width:92%; max-width:1200px; margin:-48px auto 3rem; animation:${fade}.4s;
  background:rgba(255,255,255,.68); backdrop-filter:blur(16px);
  box-shadow:0 8px 28px rgba(0,0,0,.14); border-radius:22px;
  padding:1.6rem 2rem; display:grid; gap:1.2rem;
  grid-template-columns:repeat(auto-fit,minmax(160px,1fr));
  label{font-weight:600;color:#003b70;font-size:.9rem;display:flex;flex-direction:column}
  input,select{margin-top:.45rem;padding:.5rem .75rem;font-size:.88rem;
               border:1px solid #b5c9ff;border-radius:12px;background:#edf3ff}
  .datePicker{grid-column:1/-1}               /* calendar = full row */
  .rdrCalendarWrapper{box-shadow:none}
`;

const Grid = styled.section`
  display:grid; gap:2.2rem; animation:${fade}.5s;
  max-width:1440px; margin:0 auto 4rem; padding:0 3rem;
  grid-template-columns:repeat(auto-fit,minmax(300px,1fr));
`;
const Card   = styled.div`
  cursor:pointer;border-radius:24px;overflow:hidden;position:relative;
  box-shadow:0 8px 22px rgba(0,0,0,.14);transition:.3s;
  &:hover{transform:translateY(-8px) scale(1.03);
          box-shadow:0 16px 34px rgba(0,0,0,.22)}
`;
const Thumb  = styled.img`width:100%;height:220px;object-fit:cover;`;
const Glass  = styled.div`
  position:absolute;inset:auto 0 0 0;background:rgba(255,255,255,.78);
  backdrop-filter:blur(15px);padding:1.25rem 1.5rem;
`;
const Title  = styled.h3`margin:0 0 .4rem;font-size:1.15rem;color:#003b70`;
const Row    = styled.p `margin:.12rem 0;font-size:.9rem;color:#42526e;line-height:1.33`;
const Chip   = styled.span`
  display:inline-block;font-size:.68rem;font-weight:700;color:#fff;
  padding:.15rem .6rem;border-radius:999px;margin-right:.4rem;background:${p=>p.bg};
`;
const Price  = styled.div`font-weight:700;font-size:1.06rem;color:#175dff;margin-top:.5rem`;
const Empty  = styled.p  `grid-column:1/-1;text-align:center;font-weight:600;color:#003b70`;

/* ─────────────────────────── component ────────────────────────── */
export default function CountryPage() {
  const { code } = useParams();                        // e.g. "FR"
  const nav      = useNavigate();

  /* calendar default  → tomorrow .. +7d  */
  const [range, setRange] = useState([{
    key:"selection", color:"#175dff",
    startDate: addDays(new Date(),1),
    endDate  : addDays(new Date(),8)
  }]);
  const [guests,setGuests] = useState(2);
  const [stars ,setStars ] = useState("ANY");

  const [hotels ,setHotels ] = useState([]);
  const [flights,setFlights] = useState([]);
  const [events ,setEvents ] = useState([]);

  /* load once */
  useEffect(()=>{
    (async()=>{
      const cfg={headers:{Authorization:`Bearer ${localStorage.getItem("jwt")}`}};
      const [h,f,e] = await Promise.all([
        axios.get("/api/listings/hotels",cfg),
        axios.get("/api/listings/flights",cfg),
        axios.get("/api/listings/events",cfg)
      ]);

      /* convert every date string → Date upfront */
      setHotels(h.data.map(o=>({
        ...o,
        pricePerNight:o.price,
        availableFrom: parseISO(o.availableFrom),
        availableTo  : parseISO(o.availableTo)
      })));

      setFlights(f.data.map(o=>({
        ...o,
        departureTime: toJsDate(o.departureTime),
        arrivalTime  : toJsDate(o.arrivalTime)
      })));

      setEvents(e.data.map(o=>({
        ...o,
        eventDate: toJsDate(o.eventDate)
      })));
    })();
  },[]);

  /* compute bundles */
  const bundles = useMemo(()=>{
    const [{startDate:from,endDate:to}] = range;
    const nights = nightsBetween(from,to);

    const hs = hotels.filter(h=>
      h.country===code &&
      (stars==="ANY" || (h.starRating||0)>=+stars) &&
      h.totalRooms>=guests &&
      h.availableFrom<=from && h.availableTo>=to
    );

    const fs = flights.filter(f=>
      f.country===code &&
      f.seatCapacity>=guests &&
      isSameDay(f.departureTime,from) && isSameDay(f.arrivalTime,to)
    );

    const es = events.filter(ev=>
      ev.country===code &&
      ev.ticketCapacity>=guests &&
      isWithinInterval(ev.eventDate,{start:from,end:to})
    );

    const out=[];
    hs.forEach(h=>fs.forEach(f=>es.forEach(ev=>{
      out.push({
        id   :`${h.id}-${f.id}-${ev.id}`,
        hotel:h, flight:f, event:ev, nights,
        window:`${fmt(from)} – ${fmt(to)}`,
        total :nights*h.pricePerNight + f.price + ev.price,
        thumb :h.photoUrl ?? ev.photoUrl ?? f.photoUrl
      });
    })));
    return out.sort((a,b)=>a.total-b.total).slice(0,40);
  },[code,range,guests,stars,hotels,flights,events]);

  /* hero picture (fallback Unsplash) */
  const heroImg = new Image();
  heroImg.src   = heroPic(code);
  const heroUrl = heroImg.complete ? heroPic(code)
                                   : `https://source.unsplash.com/1600x600/?${code},landmark`;

  /* ───────────────────────── render ───────────────────────── */
  return (
    <>
      <Hero $img={heroUrl}>{code}</Hero>

      <Filters>
        <label className="datePicker">
          Dates
          <DateRange
            ranges={range}
            onChange={r=>setRange([{...r.selection,color:"#175dff"}])}
            moveRangeOnFirstSelection={false}
            rangeColors={["#175dff"]}
          />
        </label>

        <label>
          Travellers
          <input type="number" min={1} max={10}
                 value={guests}
                 onChange={e=>setGuests(Math.max(1,Math.min(10,+e.target.value||1)))}/>
        </label>

        <label>
          Hotel rating
          <select value={stars} onChange={e=>setStars(e.target.value)}>
            <option value="ANY">Any</option>
            <option value="3">3★ +</option>
            <option value="4">4★ +</option>
            <option value="5">5★</option>
          </select>
        </label>
      </Filters>

      <Grid>
        {bundles.length ? bundles.map(b=>(
          <Card key={b.id} onClick={()=>nav(`/packages/${b.id}`,{state:b})}>
            <Thumb src={b.thumb || "https://via.placeholder.com/400x250"}/>
            <Glass>
              <Title>{b.hotel.title}</Title>
              <Row>
                🗓 {b.window}<br/>
                {b.nights} nights · {b.hotel.city}<br/>
                <Chip bg="#175dff">✈ {b.flight.airline}</Chip>
                <Chip bg="#e92365">🎫 {b.event.title.length>18
                                       ? b.event.title.slice(0,18)+"…" : b.event.title}</Chip>
              </Row>
              <Price>€ {b.total.toFixed(0)}</Price>
            </Glass>
          </Card>
        )) : <Empty>No bundles match your filters.</Empty>}
      </Grid>
    </>
  );
}
