import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { FaPlaneDeparture, FaHotel, FaCalendarCheck } from "react-icons/fa";
import axios from "axios";

/* ────────────────────────────────────────────────────────────
   BOOKIFY  •  login ▸ signup ▸ forgot-password splash screen
   ────────────────────────────────────────────────────────────*/

/*  Color palette  */
const pageBlue  = "#d8edff";   // right-side background
const cardBlue  = "#b4d0ff";   // auth card background
const inputBlue = "#e6f2ff";
const accent1   = "#175dff";
const accent2   = "#1c8bff";
const textDark  = "#003b70";

/*  Simple fade-in  */
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(15px); }
  to   { opacity: 1; transform: translateY(0);   }
`;

/*  Layout  */
const Wrapper = styled.div`
  display: flex; min-height: 100vh;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
`;

const ImageSide = styled.div`
  flex: 1;
  background: url("https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1352&q=80")
              center/cover;
  position: relative;
  &::after {           /* subtle dark overlay so text pops */
    content: "";
    position: absolute; inset: 0;
    background: rgba(0, 0, 0, .25);
  }
`;

const FormSide = styled.div`
  flex: 1;
  display: flex; flex-direction: column;
  align-items: center; justify-content: center;
  padding: 3rem 2rem;  background: ${pageBlue};
`;

/*  Brand banner  */
const Brand = styled.h1`
  display: flex; align-items: center; gap: .5rem;
  font-size: 3rem; font-weight: 800; letter-spacing: -1px;
  color: ${accent1}; margin: 0 0 .25rem;
  animation: ${fadeIn} .8s ease-out .1s backwards;
`;

const Tagline = styled.p`
  font-size: 1.1rem; color: ${textDark};
  margin-bottom: 2.5rem;
  animation: ${fadeIn} .8s ease-out .3s backwards;
`;

/*  Auth card  */
const Card = styled.div`
  width: 100%; max-width: 420px;
  border-radius: 20px; padding: 3rem 2.75rem;
  background: ${cardBlue};
  box-shadow: 0 12px 28px rgba(0,59,112,.18);
  animation: ${fadeIn} .8s ease-out .55s backwards;
`;

const Title = styled.h2`
  margin: 0 0 1.75rem; font-size: 2rem;
  text-align: center; color: ${textDark};
`;

const Input = styled.input`
  width: 100%; margin-bottom: 1.2rem;
  padding: .9rem 1rem; border-radius: 8px;
  border: 1px solid rgba(0,0,0,.05);
  background: ${inputBlue}; color: ${textDark};
  &::placeholder { color: #4d6b9a; }
  &:focus { outline: none; border-color: ${accent1};
    box-shadow: 0 0 0 3px rgba(23,93,255,.22);}
`;

const Button = styled.button`
  width: 100%; padding: 1rem; margin-top: .2rem;
  border: none; border-radius: 8px;
  background: linear-gradient(135deg, ${accent1}, ${accent2});
  color:#fff; font-weight:600; cursor:pointer;
  transition: transform .2s, box-shadow .2s;
  &:hover{transform:translateY(-2px);
    box-shadow:0 8px 22px rgba(23,93,255,.35);}
`;

/*  Helper text / links  */
const LinkTxt = styled.span`
  color:${accent1}; text-decoration:underline; cursor:pointer;
`;
const Small = styled.small`
  display:block; margin-top:1.25rem;
  font-size:.9rem; text-align:center; color:${textDark};
`;
const Msg = styled.div`
  margin-bottom:1rem; padding:.85rem 1rem; border-radius:8px;
  font-size:.95rem; animation:${fadeIn} .3s ease-out backwards;
  background:${p=>p.error?"#ffe6e6":"#e4ffea"};
  color:${p=>p.error?"#b00020":"#0e7a29"};
`;

/*  Decorative icon row  */
const IconsRow = styled.div`
  display:flex; gap:1rem; margin-top:1.8rem; justify-content:center;
  svg{font-size:1.6rem; color:${accent1}; opacity:0;
      animation:${fadeIn} .8s ease-out forwards;}
  svg:nth-child(1){animation-delay:.7s;}
  svg:nth-child(2){animation-delay:.85s;}
  svg:nth-child(3){animation-delay:1s;}
`;

/* ───────── COMPONENT ───────── */
export default function AuthPage() {
  /* mode: "login" | "signup" | "forgot" */
  const [mode,setMode]=useState("login");
  const [form,setForm]=useState({});
  const [msg,setMsg]=useState({text:"",error:false});

  const resetMsg=()=>setMsg({text:"",error:false});
  const onChange=e=>setForm({...form,[e.target.name]:e.target.value});

  const onSubmit=async e=>{
    e.preventDefault(); resetMsg();
    try{
      if(mode==="login"){
        const {data}=await axios.post("/api/auth/login",form);
        localStorage.setItem("jwt",data);
        // TODO navigate to dashboard
      }else if(mode==="signup"){
        await axios.post("/api/auth/signup",form);
        setMsg({text:"Account created! You can log in now.",error:false});
        setMode("login"); setForm({});
      }else{ // forgot
        await axios.post("/api/auth/forgot-password",null,{params:{email:form.email}});
        setMsg({text:"If that email exists, a reset link is on its way.",error:false});
      }
    }catch(err){
      setMsg({text:err.response?.data||"Something went wrong.",error:true});
    }
  };

  /* dynamic parts */
  const title  ={login:"Welcome back",signup:"Create account",forgot:"Forgot password"}[mode];
  const button ={login:"Log in",      signup:"Sign up",      forgot:"Send reset link"}[mode];

  const inputs=()=>{
    if(mode==="signup")return(
      <>
        <Input name="email" type="email" placeholder="Email" required onChange={onChange}/>
        <Input name="username" placeholder="Username" required onChange={onChange}/>
        <Input name="password" type="password" placeholder="Password" required onChange={onChange}/>
      </>
    );
    if(mode==="forgot")return(
        <Input name="email" type="email" placeholder="Your account email" required onChange={onChange}/>
    );
    /* login */
    return(
      <>
        <Input name="username" placeholder="Username" required onChange={onChange}/>
        <Input name="password" type="password" placeholder="Password" required onChange={onChange}/>
      </>
    );
  };

  return(
    <Wrapper>
      <ImageSide/>
      <FormSide>
        {/* brand */}
        <Brand>Bookify <FaPlaneDeparture/></Brand>
        <Tagline>Find flights, stays & events in one place</Tagline>

        {/* auth card */}
        <Card>
          <Title>{title}</Title>
          {msg.text && <Msg error={msg.error}>{msg.text}</Msg>}

          <form onSubmit={onSubmit}>
            {inputs()}

            {mode==="login"&&(
              <Small style={{textAlign:"right",marginBottom:"1rem"}}>
                <LinkTxt onClick={()=>{setMode("forgot");setForm({});resetMsg();}}>
                  Forgot password?
                </LinkTxt>
              </Small>
            )}

            <Button type="submit">{button}</Button>
          </form>

          {/* footer links */}
          {mode!=="signup"&&(
            <Small>
              New here? <LinkTxt onClick={()=>{setMode("signup");setForm({});resetMsg();}}>
                Create one
              </LinkTxt>
            </Small>
          )}
          {mode!=="login"&&(
            <Small>
              Already have an account? <LinkTxt onClick={()=>{setMode("login");setForm({});resetMsg();}}>
                Log in
              </LinkTxt>
            </Small>
          )}
        </Card>

        {/* decorative icons */}
        <IconsRow>
          <FaPlaneDeparture/>
          <FaHotel/>
          <FaCalendarCheck/>
        </IconsRow>
      </FormSide>
    </Wrapper>
  );
}
