/* ────────────────────────────────────────────────────────────────
   Bookify • CountryPage   (drop-in replacement)
   ────────────────────────────────────────────────────────────────*/
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import { DateRange } from "react-date-range";
import { addDays, format, isSameDay, isWithinInterval } from "date-fns";

import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* ---------- hero pictures (same names as on Home page) ---------- */
const hero = {
  FR: "/destinations/france.jpg",
  ES: "/destinations/barcelona.jpg",
  IT: "/destinations/italy.jpg",
  GR: "/destinations/greece.jpg",
  JP: "/destinations/japan.jpg",
  US: "/destinations/usa.jpg",
  BR: "/destinations/brazil.jpg",
  TH: "/destinations/thailand.jpg"
};

/* ---------- styling ---------- */
const fade = keyframes`from{opacity:0}to{opacity:1}`;

const Hero = styled.section`
  background:
    linear-gradient(to bottom,rgba(0,0,0,.45),rgba(0,0,0,.45)),
    url(${p => p.$img}) center/cover;
  height:270px; display:flex; align-items:center; justify-content:center;
  color:#fff; font-size:2.6rem; font-weight:800; text-shadow:0 3px 12px #000a;
  text-transform:uppercase;
`;

const Filters = styled.div`
  width:92%; max-width:1200px; margin:-48px auto 3rem; animation:${fade}.4s;
  background:rgba(255,255,255,.68); backdrop-filter:blur(18px);
  box-shadow:0 10px 34px rgba(0,0,0,.15); border-radius:24px;
  padding:1.6rem 2rem; display:grid; gap:1.4rem;
  grid-template-columns:repeat(auto-fit,minmax(160px,1fr));
  label{font-weight:600;color:#003b70;font-size:.9rem}
  input,select{margin-top:.4rem;padding:.5rem .75rem;font-size:.9rem;
               border-radius:12px;border:1px solid #b5c9ff;background:#edf3ff}
  .rdrCalendarWrapper{box-shadow:none}       /* nicer calendar look */
  .datePicker{grid-column:1/-1}              /* calendar spans full row */
`;

const Grid = styled.section`
  display:grid; gap:2.4rem; max-width:1440px; margin:0 auto 4rem;
  padding:0 3rem; grid-template-columns:repeat(auto-fit,minmax(310px,1fr));
  animation:${fade}.5s;
`;

const Card=styled.div`
  position:relative;cursor:pointer;overflow:hidden;border-radius:24px;
  box-shadow:0 8px 26px rgba(0,0,0,.15);transition:.3s;
  &:hover{transform:translateY(-8px) scale(1.03);
          box-shadow:0 16px 38px rgba(0,0,0,.22)}
`;
const Thumb=styled.img`width:100%;height:230px;object-fit:cover;`;
const Glass=styled.div`
  position:absolute;inset:auto 0 0 0;padding:1.3rem 1.5rem;
  background:rgba(255,255,255,.78);backdrop-filter:blur(16px);
`;
const Title=styled.h3`margin:0 0 .45rem;font-size:1.16rem;color:#003b70`;
const Row=styled.p`margin:.1rem 0;font-size:.9rem;color:#42526e;line-height:1.35`;
const Chip=styled.span`
  display:inline-block;font-size:.68rem;font-weight:700;color:#fff;
  padding:.15rem .6rem;border-radius:999px;margin-right:.4rem;background:${p=>p.bg};
`;
const Price=styled.div`font-weight:700;font-size:1.08rem;color:#175dff;margin-top:.55rem`;
const Empty=styled.p`grid-column:1/-1;text-align:center;font-weight:600;color:#003b70`;

/* ---------- helpers ---------- */
const diffDays=(a,b)=>Math.max(1,Math.round((b-a)/864e5));
const fmt=d=>format(new Date(d),"d MMM");

/* ---------- component ---------- */
export default function CountryPage(){
  const { code } = useParams();
  const nav      = useNavigate();

  /* tomorrow ➜ +7d, plus color so react-date-range doesn't crash */
  const [range,setRange]=useState([{
    key:"selection",
    color:"#175dff",
    startDate:addDays(new Date(),1),
    endDate  :addDays(new Date(),8)
  }]);
  const [guests,setGuests]=useState(2);
  const [stars ,setStars ]=useState("ANY");       // ANY | 3 | 4 | 5

  const [hotels ,setHotels ]=useState([]);
  const [flights,setFlights]=useState([]);
  const [events ,setEvents ]=useState([]);

  /* one-time load */
  useEffect(()=>{
    (async()=>{
      const cfg={headers:{Authorization:`Bearer ${localStorage.getItem("jwt")}`}};
      const [h,f,e]=await Promise.all([
        axios.get("/api/listings/hotels",cfg),
        axios.get("/api/listings/flights",cfg),
        axios.get("/api/listings/events",cfg)
      ]);
      setHotels (h.data.map(o=>({...o,pricePerNight:o.price})));
      setFlights(f.data);
      setEvents (e.data);
    })();
  },[]);

  /* build packages */
  const bundles=useMemo(()=>{
    const [sel]=range; if(!sel) return [];
    const {startDate:from,endDate:to}=sel;
    const nights=diffDays(from,to);

    const hs=hotels.filter(h=>{
      if(h.country!==code) return false;
      if(h.totalRooms<guests) return false;
      if(stars!=="ANY" && (h.starRating||0)<+stars) return false;
      return new Date(h.availableFrom)<=from && new Date(h.availableTo)>=to;
    });

    const fs=flights.filter(f=>{
      if(f.country!==code) return false;
      if(f.seatCapacity<guests) return false;
      return isSameDay(f.departureTime,from)&&isSameDay(f.arrivalTime,to);
    });

    const es=events.filter(ev=>{
      if(ev.country!==code) return false;
      if(ev.ticketCapacity<guests) return false;
      return isWithinInterval(new Date(ev.eventDate),{start:from,end:to});
    });

    const out=[];
    hs.forEach(h=>fs.forEach(f=>es.forEach(ev=>{
      out.push({
        id:`${h.id}-${f.id}-${ev.id}`,
        hotel:h,flight:f,event:ev,nights,
        total:nights*h.pricePerNight+f.price+ev.price,
        thumb:h.photoUrl??ev.photoUrl??f.photoUrl,
        window:`${fmt(f.departureTime)} – ${fmt(f.arrivalTime)}`
      });
    })));
    return out.sort((a,b)=>a.total-b.total).slice(0,40);
  },[code,range,guests,stars,hotels,flights,events]);

  /* hero pick (fallback Unsplash) */
  const heroImg=(hero[code]?
    hero[code]:
    `https://source.unsplash.com/1600x600/?${code},landmark`);

  /* ---------- render ---------- */
  return(
    <>
      <Hero $img={heroImg}>{code}</Hero>

      <Filters>
        <label className="datePicker">
          Dates
          <DateRange
            ranges={range}
            onChange={r=>setRange([{...r.selection,color:"#175dff"}])}
            moveRangeOnFirstSelection={false}
            rangeColors={["#175dff"]}
          />
        </label>

        <label>
          Travellers
          <input type="number" min={1} max={10}
                 value={guests}
                 onChange={e=>setGuests(Math.max(1,Math.min(10, +e.target.value||1)))}/>
        </label>

        <label>
          Hotel rating
          <select value={stars} onChange={e=>setStars(e.target.value)}>
            <option value="ANY">Any</option>
            <option value="3">3★ +</option>
            <option value="4">4★ +</option>
            <option value="5">5★</option>
          </select>
        </label>
      </Filters>

      <Grid>
        {bundles.length ? bundles.map(b=>(
          <Card key={b.id} onClick={()=>nav(`/packages/${b.id}`,{state:b})}>
            <Thumb src={b.thumb || "https://via.placeholder.com/400x250"} />
            <Glass>
              <Title>{b.hotel.title}</Title>
              <Row>
                🗓 {b.window}<br/>
                {b.nights} nights · {b.hotel.city}<br/>
                <Chip bg="#175dff">✈ {b.flight.airline}</Chip>
                <Chip bg="#e92365">🎫 {b.event.title.length>18
                                       ? b.event.title.slice(0,18)+"…" : b.event.title}</Chip>
              </Row>
              <Price>€ {b.total.toFixed(0)}</Price>
            </Glass>
          </Card>
        )):<Empty>No bundles match your filters.</Empty>}
      </Grid>
    </>
  );
}
