import React, { useState } from "react";
import { useLocation }     from "react-router-dom";
import styled, { keyframes } from "styled-components";

/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ animations / layout ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
const fadeIn = keyframes`
  from { opacity:0; transform:translateY(10px); }
  to   { opacity:1; transform:translateY(0); }
`;

const Wrapper = styled.div`
  max-width: 1000px;
  margin: 2rem auto;
  padding: 0 1.5rem;
  font-family: "Segoe UI", sans-serif;
  animation: ${fadeIn} 0.5s ease-out;
`;

const Banner = styled.div`
  height: 200px;
  background: ${p => p.$gradient};
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  padding: 1rem;
  box-shadow: 0 4px 12px rgba(0,0,0,.1);

  @media (max-width: 768px) { height: 150px; }
`;

const BannerIcon  = styled.span`
  position:absolute; top:1rem; left:1rem; font-size:2rem;
`;
const BannerTitle = styled.h2`
  margin:0; font-size:1.8rem; font-weight:700; color:#fff;
  text-shadow:0 2px 4px rgba(0,0,0,.3); text-align:center;
`;

const H2   = styled.h2`
  margin:1.5rem 0 .8rem; color:#003b70; font-size:1.8rem; font-weight:700;
`;
const Box  = styled.div`
  background:#fff; border-radius:14px; padding:1.5rem;
  box-shadow:0 6px 18px rgba(0,0,0,.1); margin-bottom:1.5rem;
  font-size:.95rem; color:#42526e; line-height:1.5;
`;
const Label = styled.span`
  display:inline-block; font-weight:700; color:#175dff;
  margin-right:.6rem; font-size:1rem;
`;

const BookBtn = styled.button`
  display:block; margin:1.5rem auto; padding:.9rem 2rem;
  border:none; border-radius:30px; background:#175dff; color:#fff;
  font-weight:600; font-size:1rem; cursor:pointer; transition:.3s;
  &:hover{ transform:translateY(-3px);
           box-shadow:0 8px 24px rgba(23,93,255,.45); background:#004bff; }
`;

const NotIncluded = styled.p`
  color:#666; font-style:italic; margin:.5rem 0;
`;

/*  modal */
const Modal = styled.div`
  position:fixed; inset:0; background:rgba(0,0,0,.5);
  display:${p=>p.$isOpen?"flex":"none"}; align-items:center; justify-content:center;
  z-index:1000;
`;
const ModalContent = styled.div`
  background:#fff; padding:2rem; border-radius:10px; width:90%; max-width:400px;
  text-align:center; box-shadow:0 4px 12px rgba(0,0,0,.2);
`;
const ModalButton = styled.button`
  padding:.8rem 1.5rem; margin:.5rem; border:none; border-radius:25px;
  font-weight:600; cursor:pointer; transition:.3s;
  &:hover{ transform:translateY(-2px); box-shadow:0 4px 12px rgba(0,0,0,.1); }
`;
const PayNowBtn       = styled(ModalButton)`background:#175dff; color:#fff;`;
const PayOnArrivalBtn = styled(ModalButton)`background:#e92365; color:#fff;`;
const ErrorMessage    = styled.p`color:#e92365; margin-top:1rem;`;

/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ component ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
export default function PackagePage() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [error,       setError]       = useState(null);

  const { state:data } = useLocation();
  if (!data)
    return <p style={{textAlign:"center",color:"#e92365"}}>No package data.</p>;

  const { hotel, flight, event, total, nights, window } = data;

  /* hero gradient + icon */
  const hasFlight = !!flight, hasEvent = !!event;
  const gradient  = hasFlight
      ? hasEvent ? "linear-gradient(135deg,#175dff,#e92365)"
                 : "linear-gradient(135deg,#175dff,#4b9bff)"
      : hasEvent ? "linear-gradient(135deg,#e92365,#ff6b9b)"
                 : "linear-gradient(135deg,#003b70,#4b9bff)";
  const icon = hasFlight ? "‚úà" : hasEvent ? "üé´" : "üè®";

  /* -------------- booking -------------- */
  const handleBook = async (payNow) => {
    /* split the ‚Äúdd MMM ‚Äì dd MMM‚Äù window coming from CountryPage */
    const [checkInStr, checkOutStr] = window.split(" ‚Äì ");
    const checkIn  = new Date(checkInStr ).toISOString().split("T")[0];
    const checkOut = new Date(checkOutStr).toISOString().split("T")[0];

    /* helper that posts ONE booking */
    const postBooking = (listing, type) => {
      const dto = {
        listingId : listing.id,
        type,
        checkIn   : type==="HOTEL" ? checkIn  : null,
        checkOut  : type==="HOTEL" ? checkOut : null,
        numGuests : 2,
        payNow
      };
      return fetch("/api/bookings", {
        method :"POST",
        headers:{
          "Content-Type":"application/json",
          "Authorization":`Bearer ${localStorage.getItem("jwt")||""}`
        },
        body:JSON.stringify(dto)
      }).then(r=>{
        if(!r.ok) throw new Error(`Booking ${type} failed`);
        return r.json();
      });
    };

    try{
      await Promise.all([
        postBooking(hotel , "HOTEL"),
        flight && postBooking(flight,"FLIGHT"),
        event  && postBooking(event ,"EVENT")
      ].filter(Boolean));

      setError(null);
      setIsModalOpen(false);
      alert("Package booked ‚Äì confirmation e-mails on their way!");

    }catch(err){
      setError(err.message);
    }
  };

  /* -------------- render -------------- */
  return (
    <Wrapper>
      <Banner $gradient={gradient}>
        <BannerIcon>{icon}</BannerIcon>
        <BannerTitle>{hotel.title}</BannerTitle>
      </Banner>

      {/* HOTEL */}
      <Box>
        <Label>üè® Hotel</Label>
        {hotel.city} ¬∑ {nights} nights ¬∑ ‚Ç¨{(nights*hotel.pricePerNight).toFixed(0)}
        <br/>{hotel.description || "A comfortable stay in the heart of the city."}
      </Box>

      {/* FLIGHT */}
      <Box>
        <Label>‚úà Flight</Label>
        {flight ? (
          <>
            {flight.departure} ‚Üí {flight.arrival}<br/>
            Departs  {new Date(flight.departureTime).toLocaleString()}<br/>
            Returns  {new Date(flight.arrivalTime) .toLocaleString()}<br/>
            ‚Ç¨{flight.price}
          </>
        ):<NotIncluded>No flight included.</NotIncluded>}
      </Box>

      {/* EVENT */}
      <Box>
        <Label>üé´ Event</Label>
        {event ? (
          <>
            {event.title} ‚Äì {new Date(event.eventDate).toLocaleString()}<br/>
            Venue: {event.venue} ¬∑ ‚Ç¨{event.price}
          </>
        ):<NotIncluded>No event included.</NotIncluded>}
      </Box>

      <H2>Total ‚Ç¨{total.toFixed(0)}</H2>
      <BookBtn onClick={()=>setIsModalOpen(true)}>Book Now</BookBtn>

      {/* modal */}
      <Modal $isOpen={isModalOpen}>
        <ModalContent>
          <h3>Choose payment option</h3>
          <PayNowBtn       onClick={()=>handleBook(true )}>Pay Now</PayNowBtn>
          <PayOnArrivalBtn onClick={()=>handleBook(false)}>Pay on arrival</PayOnArrivalBtn>
          <button style={{marginTop:"1rem"}}
                  onClick={()=>setIsModalOpen(false)}>Cancel</button>
          {error && <ErrorMessage>{error}</ErrorMessage>}
        </ModalContent>
      </Modal>
    </Wrapper>
  );
}
