import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";

/**
 * AuthPage â€“ splitâ€‘screen layout
 *   â€¢ Ocean vacation image on the left
 *   â€¢ Babyâ€‘blue page background on the right
 *   â€¢ Darker royalâ€‘blue auth card for contrast
 *   â€¢ Login / signup toggle + "Forgot password?" link
 */

/* Palette */
const lightBlue = "#eaf5ff";  // page background
const cardBlue = "#c8deff";   // auth card (darker than bg)
const accentBlue1 = "#286efa"; // buttons / accents
const accentBlue2 = "#1ea7ff"; // gradient end
const textDark = "#113b6d";

const Wrapper = styled.div`
  display: flex;
  min-height: 100vh;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
`;

const ImageSide = styled.div`
  flex: 1;
  background-image: url("https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1352&q=80");
  background-size: cover;
  background-position: center;
`;

const FormSide = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 3rem;
  background: ${lightBlue}; /* babyâ€‘blue full col */
`;

const Card = styled.div`
  width: 100%;
  max-width: 420px;
  border-radius: 16px;
  background: ${cardBlue}; /* darker blue box */
  box-shadow: 0 14px 32px rgba(0, 0, 0, 0.12);
  backdrop-filter: blur(2px);
  padding: 3rem 2.5rem;
`;

const Title = styled.h2`
  margin: 0 0 1.6rem 0;
  font-size: 1.9rem;
  color: ${textDark};
  text-align: center;
`;

const StyledInput = styled.input`
  width: 100%;
  margin-bottom: 1.2rem;
  padding: 0.85rem 1rem;
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 8px;
  font-size: 0.95rem;
  background: #ffffff;
  &:focus {
    outline: none;
    border-color: ${accentBlue1};
    box-shadow: 0 0 0 3px rgba(40, 110, 250, 0.18);
  }
`;

const Button = styled.button`
  width: 100%;
  padding: 0.9rem;
  background: linear-gradient(135deg, ${accentBlue1}, ${accentBlue2});
  border: none;
  border-radius: 8px;
  color: #fff;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(40, 110, 250, 0.3);
  }
  &:active {
    transform: translateY(0);
    box-shadow: none;
  }
`;

const Toggle = styled.p`
  margin-top: 1.5rem;
  font-size: 0.9rem;
  text-align: center;
  color: ${textDark};
  cursor: pointer;
  span {
    color: ${accentBlue1};
    text-decoration: underline;
  }
`;

const Forgot = styled.a`
  display: block;
  margin-top: 0.8rem;
  margin-bottom: 1.2rem;
  font-size: 0.85rem;
  text-align: right;
  color: ${accentBlue1};
  text-decoration: underline;
  cursor: pointer;
`;

const Error = styled.div`
  margin-bottom: 1rem;
  padding: 0.75rem 1rem;
  background: #ffe6e6;
  color: #b00020;
  border-radius: 8px;
  font-size: 0.9rem;
`;

function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({});
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      if (isLogin) {
        const { data: token } = await axios.post("/api/auth/login", form);
        localStorage.setItem("jwt", token);
        // ðŸ‘‰  TODO: navigate to dashboard
      } else {
        await axios.post("/api/auth/signup", form);
        setIsLogin(true);
      }
    } catch (err) {
      setError(err.response?.data || "Something went wrong. Please try again.");
    }
  };

  return (
    <Wrapper>
      <ImageSide />
      <FormSide>
        <Card>
          <Title>{isLogin ? "Welcome back" : "Create account"}</Title>
          {error && <Error>{error}</Error>}

          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <StyledInput
                name="email"
                type="email"
                placeholder="Email"
                required
                onChange={handleChange}
              />
            )}

            <StyledInput
              name="username"
              placeholder="Username"
              required
              onChange={handleChange}
            />

            <StyledInput
              name="password"
              type="password"
              placeholder="Password"
              required
              onChange={handleChange}
            />

            {isLogin && <Forgot href="/forgot-password">Forgot password?</Forgot>}

            <Button type="submit">{isLogin ? "Log in" : "Sign up"}</Button>
          </form>

          <Toggle onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? "New here? " : "Already have an account? "}
            <span>{isLogin ? "Create one" : "Log in"}</span>
          </Toggle>
        </Card>
      </FormSide>
    </Wrapper>
  );
}

export default AuthPage;
