import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import AuthPage          from "./pages/AuthPage";
import ResetPasswordPage from "./pages/ResetPasswordPage";
import HomePage          from "./pages/HomePage";
import AccountPage       from "./pages/AccountPage";
import CountryPage       from "./pages/CountryPage";
import PackagePage       from "./pages/PackagePage";
import CategoryPage      from "./pages/CategoryPage";
import BookPage from "./pages/BookPage";
import AdminPage from "./pages/AdminPage";

function ProtectedRoute({ children, allowedRole }) {
  const [isAuthorized, setIsAuthorized] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("/login");
      return;
    }

    axios
      .get("/api/users/me", { headers: { Authorization: `Bearer ${token}` } })
      .then(({ data }) => {
        if (data.role === allowedRole) {
          setIsAuthorized(true);
        } else {
          setIsAuthorized(false);
          navigate("/home");
        }
      })
      .catch(() => {
        setIsAuthorized(false);
        navigate("/login");
      });
  }, [navigate, allowedRole]);

  if (isAuthorized === null) {
    return <div>Loading...</div>;
  }

  return isAuthorized ? children : null;
}
export default function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/"                  element={<AuthPage />} />
        <Route path="/reset-password"    element={<ResetPasswordPage />} />
        <Route path="/home"              element={<HomePage />} />
        <Route path="/account"           element={<AccountPage />} />
        <Route path="/destinations/:code"element={<CountryPage />} />
        <Route path="/packages/:pid"     element={<PackagePage />} />

        {/* single-category catalogues */}
        <Route path="/flights" element={<CategoryPage category="flights" />} />
        <Route path="/hotels"  element={<CategoryPage category="hotels"  />} />
        <Route path="/events"  element={<CategoryPage category="events"  />} />

        <Route path="/book/:category/:id" element={<BookPage />} />
        <Route
          path="/admin"
          element={
            <ProtectedRoute allowedRole="ROLE_ADMIN">
              <AdminPage />
            </ProtectedRoute>}
        />

      </Routes>
    </BrowserRouter>
  );
}
