// src/pages/AdminListingsPage.jsx
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import Header from "../components/Header";

const pageBlue = "#d8edff";
const fadeIn = keyframes`from{opacity:0;transform:translateY(15px)}to{opacity:1;transform:translateY(0)}`;
const accent1  = "#175dff";
const textDark = "#003b70";

const Wrapper = styled.div`
  min-height: 100vh;
  background: ${pageBlue};
  display: flex;
  flex-direction: column;
`;
const Hero = styled.section`
  background: #fff;
  color: ${textDark};
  text-align: center;
  padding: 2rem 1rem;
  animation: ${fadeIn} .7s ease-out;
`;
const HeroTitle = styled.h1`
  margin: 0;
  font-size: 2.5rem;
`;
const Grid = styled.section`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  gap: 1.5rem;
  padding: 2rem;
`;
const Card = styled.div`
  position: relative;
  border-radius: 12px;
  overflow: hidden;
  height: 220px;
  box-shadow: 0 4px 10px rgba(0,0,0,0.1);
  background: #fff;
  transition: transform .3s;
  &:hover { transform: translateY(-4px); }
`;
const Img = styled.img`
  width: 100%; height: 120px; object-fit: cover;
`;
const Content = styled.div`
  padding: 1rem;
`;
const Title = styled.h3`
  margin: 0 0 .5rem;
`;
const Button = styled.button`
  margin-right: .5rem;
  padding: .4rem 1rem;
  background: ${accent1};
  color: #fff;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover { background: #004bff; }
`;
const AddNew = styled(Button)`
  grid-column: 1/-1;
  justify-self: center;
`;

export default function AdminListingsPage() {
  const { category } = useParams();
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    axios.get(`/api/listings/${category}`)
      .then(res => setItems(res.data))
      .catch(console.error);
  }, [category]);

  const handleEdit = id => navigate(`/admin/${category}/${id}/edit`);
  const handleDelete = id => {
    const token = localStorage.getItem("jwt");
    axios.delete(`/api/listings/${category}/${id}`, { headers:{ Authorization:`Bearer ${token}` } })
      .then(() => setItems(prev => prev.filter(i=>i.id!==id)))
      .catch(console.error);
  };

  return (
    <Wrapper>
      <Header />
      <Hero>
        <HeroTitle>Manage {category.charAt(0).toUpperCase()+category.slice(1)}</HeroTitle>
      </Hero>
      <Grid>
        {items.map(item => (
          <Card key={item.id}>
            <Img src={item.imageUrl} alt={item.title} />
            <Content>
              <Title>{item.title}</Title>
              <Button onClick={()=>handleEdit(item.id)}>Edit</Button>
              <Button onClick={()=>handleDelete(item.id)}>Delete</Button>
            </Content>
          </Card>
        ))}
        <AddNew onClick={()=>navigate(`/admin/${category}/new`)}>Add New</AddNew>
      </Grid>
    </Wrapper>
  );
}
