// src/pages/AccountPage.jsx
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

const Wrapper = styled.div`max-width:900px;margin:3rem auto;padding:0 1rem;`;
const Tabs = styled.div`display:flex;gap:2rem;border-bottom:2px solid #e0e0e0;margin-bottom:2rem;`;
const Tab = styled.button`
  background:none;border:none;font-size:1.1rem;font-weight:600;cursor:pointer;
  padding:.6rem 0;color:${p=>p.active==="true"?"#175dff":"#777"};
  border-bottom:3px solid ${p=>p.active==="true"?"#175dff":"transparent"};
`;
const Section = styled.div`animation:fade .3s;@keyframes fade{from{opacity:0}to{opacity:1}}`;
const BookingCard = styled.div`border:1px solid #e0e0e0;border-radius:6px;padding:1rem;margin-bottom:1rem;`;
const ReviewInput = styled.textarea`width:100%;min-height:80px;padding:0.5rem;margin-top:0.5rem;`;
const SubmitReviewButton = styled.button`
  padding:0.6rem 1.2rem;background:#28a745;color:#fff;border:none;border-radius:6px;margin-top:0.5rem;
`;

export default function AccountPage() {
  const [tab, setTab]         = useState("profile");
  const [user, setUser]       = useState(null);
  const [bookings, setBookings] = useState([]);
  const [reviews, setReviews]   = useState({});
  const [newReview, setNewReview] = useState({});
  const [error, setError]       = useState(null);

  // 1) Load current user
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("Not authenticated — redirecting to login…");
      setTimeout(() => window.location.href = "/", 2000);
      return;
    }
    axios.get("/api/users/me", { headers:{ Authorization:`Bearer ${token}` } })
      .then(res => setUser(res.data))
      .catch(err => setError("Failed to load profile"));
  }, []);

  // 2) Once we have user.id, load their bookings
  useEffect(() => {
    if (!user || !user.id) return;
    const token = localStorage.getItem("jwt");
    axios.get(`/api/bookings/user/${user.id}`, { headers:{ Authorization:`Bearer ${token}` } })
      .then(res => {
        setBookings(res.data);
        // 3) For each booking, load its reviews
        res.data.forEach(bk => {
          axios.get(`/api/reviews/booking/${bk.id}`, { headers:{ Authorization:`Bearer ${token}` } })
            .then(r => setReviews(prev => ({ ...prev, [bk.id]: r.data })))
            .catch(() => {/* ignore individual review errors */});
        });
      })
      .catch(() => setError("Failed to load bookings"));
  }, [user]);

  const handleReviewSubmit = bookingId => {
    const token = localStorage.getItem("jwt");
    const text  = newReview[bookingId] || "";
    if (!text.trim()) return alert("Please enter a review");
    axios.post("/api/reviews", {
      bookingId,
      comment: text,
      rating: 5
    }, { headers:{ Authorization:`Bearer ${token}` } })
    .then(res => {
      setReviews(prev => ({
        ...prev,
        [bookingId]: [res.data, ...(prev[bookingId]||[])]
      }));
      setNewReview(prev => ({ ...prev, [bookingId]: "" }));
    })
    .catch(() => alert("Failed to submit review"));
  };

  if (error) return <p style={{color:"red"}}>{error}</p>;
  if (!user) return <p>Loading your account…</p>;

  return (
    <Wrapper>
      <h1>My account</h1>
      <Tabs>
        <Tab active={tab==="profile"?"true":"false"} onClick={()=>setTab("profile")}>
          Profile
        </Tab>
        <Tab active={tab==="bookings"?"true":"false"} onClick={()=>setTab("bookings")}>
          My bookings
        </Tab>
      </Tabs>

      {tab==="profile" && (
        <Section>
          <h2>Welcome, {user.firstName} {user.lastName}</h2>
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
          {/* Add your edit form here… */}
        </Section>
      )}

      {tab==="bookings" && (
        <Section>
          <h2>Upcoming & past trips</h2>
          {bookings.length === 0
            ? <p>You have no bookings yet.</p>
            : bookings.map(bk => (
              <BookingCard key={bk.id}>
                <h3>{bk.listing?.title||"Untitled"} ({bk.type})</h3>
                <p>Check-in: {bk.checkIn} | Check-out: {bk.checkOut}</p>
                <p>Status: {bk.status}</p>

                <h4>Leave a review</h4>
                <ReviewInput
                  value={newReview[bk.id]||""}
                  onChange={e => setNewReview(prev => ({
                    ...prev, [bk.id]: e.target.value
                  }))}
                />
                <SubmitReviewButton onClick={()=>handleReviewSubmit(bk.id)}>
                  Submit Review
                </SubmitReviewButton>

                {reviews[bk.id]?.length > 0 && (
                  <>
                    <h4>Your Reviews</h4>
                    {reviews[bk.id].map(rv => (
                      <p key={rv.id}>{rv.comment}</p>
                    ))}
                  </>
                )}
              </BookingCard>
            ))
          }
        </Section>
      )}
    </Wrapper>
  );
}
