import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";

const Wrapper = styled.div`max-width:900px;margin:3rem auto;padding:0 1rem;`;
const Tabs = styled.div`display:flex;gap:2rem;border-bottom:2px solid #e0e0e0;margin-bottom:2rem;`;
const Tab = styled.button`
  background:none;border:none;font-size:1.1rem;font-weight:600;cursor:pointer;
  padding:.6rem 0;color:${p=>p.active?"#175dff":"#777"};
  border-bottom:3px solid ${p=>p.active?"#175dff":"transparent"};
`;
const Section = styled.div`animation:fade .3s;@keyframes fade{from{opacity:0}to{opacity:1}}`;
const FormGroup = styled.div`margin-bottom:1rem;`;
const Label = styled.label`display:block;margin-bottom:0.3rem;color:#003b70;font-weight:600;`;
const Input = styled.input`width:100%;padding:0.6rem;border:1px solid #d1e0ff;border-radius:6px;`;
const SaveButton = styled.button`
  padding:0.7rem 1.5rem;background:#175dff;color:#fff;border:none;border-radius:6px;cursor:pointer;
  &:hover{background:#004bff;}
`;
const DeleteButton = styled.button`
  padding:0.7rem 1.5rem;background:#e92365;color:#fff;border:none;border-radius:6px;cursor:pointer;margin-left:1rem;
  &:hover{background:#ff6b9b;}
`;
const BookingCard = styled.div`border:1px solid #e0e0e0;border-radius:6px;padding:1rem;margin-bottom:1rem;`;
const ReviewInput = styled.textarea`width:100%;min-height:80px;padding:0.5rem;margin-top:0.5rem;`;
const SubmitReviewButton = styled.button`
  padding:0.6rem 1.2rem;background:#28a745;color:#fff;border:none;border-radius:6px;margin-top:0.5rem;
`;

export default function AccountPage() {
  const [tab, setTab] = useState("profile");
  const [user, setUser] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [reviews, setReviews] = useState({});
  const [newReview, setNewReview] = useState({});
  const [editUser, setEditUser] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("No authentication token found. Please log in.");
      return;
    }

    // Fetch user profile
    axios.get("/api/users/me", { headers: { Authorization: `Bearer ${token}` } })
      .then(response => {
        setUser(response.data);
        setEditUser({ ...response.data, password: "" }); // Exclude password initially
      })
      .catch(err => setError("Failed to load profile: " + err.message));

    // Fetch bookings
    axios.get("/api/bookings/user/1", { headers: { Authorization: `Bearer ${token}` } }) // Replace 1 with user ID
      .then(response => {
        setBookings(response.data);
        // Fetch reviews for each booking
        response.data.forEach(booking => {
          axios.get(`/api/reviews/booking/${booking.id}`, { headers: { Authorization: `Bearer ${token}` } })
            .then(r => setReviews(prev => ({ ...prev, [booking.id]: r.data })))
            .catch(err => console.error(`Failed to load reviews for booking ${booking.id}:`, err));
        });
      })
      .catch(err => setError("Failed to load bookings: " + err.message));
  }, []);

  const handleSave = () => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("No authentication token found. Please log in.");
      return;
    }

    axios.patch("/api/users/me", {
      email: editUser.email,
      username: editUser.username,
      password: editUser.password || undefined, // Only send if changed
      firstName: editUser.firstName,
      lastName: editUser.lastName,
      age: editUser.age
    }, { headers: { Authorization: `Bearer ${token}` } })
      .then(response => {
        setUser(response.data);
        setError(null);
        alert("Profile updated successfully!");
      })
      .catch(err => setError("Failed to update profile: " + err.message));
  };

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete your account? This action cannot be undone.")) {
      const token = localStorage.getItem("jwt");
      if (!token) {
        setError("No authentication token found. Please log in.");
        return;
      }

      axios.delete("/api/users/me", { headers: { Authorization: `Bearer ${token}` } })
        .then(() => {
          localStorage.removeItem("jwt");
          alert("Account deleted successfully. You have been logged out.");
          window.location.href = "/login"; // Redirect to login page
        })
        .catch(err => setError("Failed to delete account: " + err.message));
    }
  };

  const handleReviewSubmit = (bookingId) => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      setError("No authentication token found. Please log in.");
      return;
    }

    axios.post("/api/reviews", {
      bookingId,
      comment: newReview[bookingId] || "",
      rating: 5 // Default rating; adjust if you add a rating input
    }, { headers: { Authorization: `Bearer ${token}` } })
      .then(response => {
        setReviews(prev => ({
          ...prev,
          [bookingId]: [response.data, ...(prev[bookingId] || [])]
        }));
        setNewReview(prev => ({ ...prev, [bookingId]: "" }));
        setError(null);
      })
      .catch(err => setError(`Failed to add review for booking ${bookingId}: ${err.message}`));
  };

  return (
    <Wrapper>
      <h1>My account</h1>
      <Tabs>
        <Tab active={tab === "profile"} onClick={() => setTab("profile")}>Profile</Tab>
        <Tab active={tab === "bookings"} onClick={() => setTab("bookings")}>My bookings</Tab>
      </Tabs>

      {error && <p style={{ color: "#e92365" }}>{error}</p>}

      {tab === "profile" && user && (
        <Section>
          <h2>Edit details</h2>
          <FormGroup>
            <Label>Email</Label>
            <Input value={editUser.email || ""} onChange={e => setEditUser({ ...editUser, email: e.target.value })} />
          </FormGroup>
          <FormGroup>
            <Label>Username</Label>
            <Input value={editUser.username || ""} onChange={e => setEditUser({ ...editUser, username: e.target.value })} />
          </FormGroup>
          <FormGroup>
            <Label>Password</Label>
            <Input type="password" value={editUser.password || ""} onChange={e => setEditUser({ ...editUser, password: e.target.value })} placeholder="Leave blank to keep unchanged" />
          </FormGroup>
          <FormGroup>
            <Label>First Name</Label>
            <Input value={editUser.firstName || ""} onChange={e => setEditUser({ ...editUser, firstName: e.target.value })} />
          </FormGroup>
          <FormGroup>
            <Label>Last Name</Label>
            <Input value={editUser.lastName || ""} onChange={e => setEditUser({ ...editUser, lastName: e.target.value })} />
          </FormGroup>
          <FormGroup>
            <Label>Age</Label>
            <Input type="number" value={editUser.age || ""} onChange={e => setEditUser({ ...editUser, age: +e.target.value || null })} />
          </FormGroup>
          <FormGroup>
            <Label>Role (read-only)</Label>
            <Input value={user.role} disabled />
          </FormGroup>
          <SaveButton onClick={handleSave}>Save Changes</SaveButton>
          <DeleteButton onClick={handleDelete}>Delete Account</DeleteButton>
        </Section>
      )}

      {tab === "bookings" && (
        <Section>
          <h2>Upcoming & past trips</h2>
          {bookings.length ? (
            bookings.map(booking => (
              <BookingCard key={booking.id}>
                <h3>{booking.listing.title} ({booking.type})</h3>
                <p>Check-in: {booking.checkIn} | Check-out: {booking.checkOut}</p>
                <p>Total: ${booking.listing.price * (booking.numGuests || 1)}</p>
                <p>Status: {booking.status}</p>
                <ReviewInput
                  value={newReview[booking.id] || ""}
                  onChange={e => setNewReview({ ...newReview, [booking.id]: e.target.value })}
                  placeholder="Add a review..."
                />
                <SubmitReviewButton onClick={() => handleReviewSubmit(booking.id)}>Submit Review</SubmitReviewButton>
                {reviews[booking.id] && reviews[booking.id].length > 0 && (
                  <div>
                    <h4>Reviews</h4>
                    {reviews[booking.id].map(review => (
                      <p key={review.id}>{review.comment}</p>
                    ))}
                  </div>
                )}
              </BookingCard>
            ))
          ) : (
            <p>You have no bookings yet.</p>
          )}
        </Section>
      )}
    </Wrapper>
  );
}
