import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import Header from "../components/Header";

const fadeIn = keyframes`
  from { opacity:0; transform:translateY(10px) }
  to   { opacity:1; transform:translateY(0) }
`;

const Wrapper = styled.div`background:#f0f4f8;min-height:100vh;`;
const Content = styled.div`
  background:#fff;max-width:600px;margin:2rem auto;padding:2rem;
  border-radius:10px;box-shadow:0 4px 12px rgba(0,0,0,.1);
  animation:${fadeIn}.5s ease-out;
`;
const Title = styled.h2`color:#175dff;text-align:center;`;
const Form = styled.form`display:flex;flex-direction:column;gap:1rem;`;
const Field = styled.div`display:flex;flex-direction:column;`;
const Label = styled.label`font-weight:600;margin-bottom:.25rem;`;
const Input = styled.input`
  padding:.6rem;border:1px solid #ccc;border-radius:6px;font-size:1rem;
`;
const ButtonRow = styled.div`
  display:flex;justify-content:space-between;margin-top:1.5rem;
`;
const Button = styled.button`
  padding:.8rem 1rem;border:none;border-radius:6px;color:#fff;
  background:${p=>p.secondary?"#aaa":"#175dff"};flex:1;margin:0 .25rem;
  cursor:pointer;
  &:hover{background:${p=>p.secondary?"#888":"#004bff"};}
`;
const Error = styled.p`color:#e92365;text-align:center;`;

export default function AdminEditUserPage() {
  const { id } = useParams();
  const isNew = false; // admin cannot create new users
  const [user, setUser] = useState({
    username: "", email: "", firstName: "", lastName: "", age: "", role: ""
  });
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);
  const nav = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) return nav("/login");
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    axios
      .get(`/api/users/${id}`)
      .then(({ data }) => {
        setUser({
          username: data.username,
          email: data.email,
          firstName: data.firstName,
          lastName: data.lastName,
          age: data.age || "",
          role: data.role,
        });
        setLoading(false);
      })
      .catch(() => {
        setErr("Failed to load user");
        setLoading(false);
      });
  }, [id, nav]);

  const onChange = (e) => {
    const { name, value } = e.target;
    setUser((u) => ({ ...u, [name]: value }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    axios
      .put(`/api/users/${id}`, { ...user })
      .then(() => nav("/admin/users"))
      .catch(() => setErr("Save failed"));
  };

  if (loading) return <Wrapper><Header/><Content><p>Loadingâ€¦</p></Content></Wrapper>;

  return (
    <Wrapper>
      <Header />
      <Content>
        <Title>Edit USER</Title>
        {err && <Error>{err}</Error>}
        <Form onSubmit={onSubmit}>
          <Field>
            <Label>Username</Label>
            <Input name="username" value={user.username} onChange={onChange} disabled />
          </Field>
          <Field>
            <Label>Email</Label>
            <Input name="email" value={user.email} onChange={onChange} required />
          </Field>
          <Field>
            <Label>First Name</Label>
            <Input name="firstName" value={user.firstName} onChange={onChange} />
          </Field>
          <Field>
            <Label>Last Name</Label>
            <Input name="lastName" value={user.lastName} onChange={onChange} />
          </Field>
          <Field>
            <Label>Age</Label>
            <Input type="number" name="age" value={user.age} onChange={onChange} />
          </Field>
          <Field>
            <Label>Role</Label>
            <select name="role" value={user.role} onChange={onChange}>
              <option value="ROLE_USER">User</option>
              <option value="ROLE_ADMIN">Admin</option>
            </select>
          </Field>
          <ButtonRow>
            <Button secondary type="button" onClick={() => nav("/admin/users")}>
              Cancel
            </Button>
            <Button type="submit">Save</Button>
          </ButtonRow>
        </Form>
      </Content>
    </Wrapper>
  );
}
