import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";
import { useNavigate, useEffect, useState } from "react";

import AuthPage from "./pages/AuthPage";
import ResetPasswordPage from "./pages/ResetPasswordPage";
import HomePage from "./pages/HomePage";
import AccountPage from "./pages/AccountPage";
import CountryPage from "./pages/CountryPage";
import PackagePage from "./pages/PackagePage";
import CategoryPage from "./pages/CategoryPage";
import BookPage from "./pages/BookPage";
import AdminPage from "./pages/AdminPage";
import AdminListingsPage from "./pages/AdminListingsPage";
import AdminEditListingPage from "./pages/AdminEditListingPage";

// central protected‐route wrapper
function ProtectedRoute({ children, allowedRole }) {
  const [isAuthorized, setIsAuthorized] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      localStorage.setItem("error", "Please log in to access this page");
      return navigate("/login");
    }

    axios
      .get("/api/users/me", { headers: { Authorization: `Bearer ${token}` } })
      .then(({ data }) => {
        // must match exactly
        if (allowedRole && data.role !== allowedRole) {
          localStorage.setItem("error", "Unauthorized — redirecting");
          setIsAuthorized(false);
          return navigate("/login");
        }
        setIsAuthorized(true);
      })
      .catch(() => {
        localStorage.setItem("error", "Session invalid — please log in again");
        setIsAuthorized(false);
        navigate("/login");
      });
  }, [allowedRole, navigate]);

  if (isAuthorized === null) return <div>Loading…</div>;
  return isAuthorized ? children : null;
}

export default function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* public */}
        <Route path="/" element={<AuthPage />} />
        <Route path="/login" element={<AuthPage />} />
        <Route path="/reset-password" element={<ResetPasswordPage />} />

        {/* only users */}
        <Route
          path="/home"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <HomePage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/account"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <AccountPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/destinations/:code"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <CountryPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/packages/:pid"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <PackagePage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/flights"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <CategoryPage category="flights" />
            </ProtectedRoute>
          }
        />
        <Route
          path="/hotels"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <CategoryPage category="hotels" />
            </ProtectedRoute>
          }
        />
        <Route
          path="/events"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <CategoryPage category="events" />
            </ProtectedRoute>
          }
        />
        <Route
          path="/book/:category/:id"
          element={
            <ProtectedRoute allowedRole="ROLE_USER">
              <BookPage />
            </ProtectedRoute>
          }
        />

        {/* admin dashboard */}
        <Route
          path="/admin"
          element={
            <ProtectedRoute allowedRole="ROLE_ADMIN">
              <AdminPage />
            </ProtectedRoute>
          }
        />

        {/* one dynamic “manage” page for all four categories */}
        <Route
          path="/admin/:category"
          element={
            <ProtectedRoute allowedRole="ROLE_ADMIN">
              <AdminListingsPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/:category/new"
          element={
            <ProtectedRoute allowedRole="ROLE_ADMIN">
              <AdminEditListingPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/:category/:id/edit"
          element={
            <ProtectedRoute allowedRole="ROLE_ADMIN">
              <AdminEditListingPage />
            </ProtectedRoute>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}
