/* src/pages/CountryPage.jsx */
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import { DateRange } from "react-date-range";
import { addDays } from "date-fns";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€ styles â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const fade = keyframes`from{opacity:0}to{opacity:1}`;
const Hero = styled.section`
  background: url(${p => p.$img}) center/cover;
  height: 260px;
  display: flex; align-items: center; justify-content: center;
  color: #fff; font-size: 2.6rem; font-weight: 800;
  text-shadow: 0 2px 8px #0006;
`;
const Filters = styled.div`
  width: 95%; max-width: 1080px; margin: -40px auto 2.5rem;
  padding: 1.25rem 1.75rem; border-radius: 20px;
  backdrop-filter: blur(16px);
  background: rgba(255, 255, 255, .55);
  box-shadow: 0 8px 28px rgba(0, 0, 0, .12);
  animation: ${fade} .5s ease-out;
`;
const Grid = styled.section`
  display: grid; gap: 2rem; max-width: 1400px; margin: 0 auto 4rem; padding: 0 3rem;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  animation: ${fade} .6s ease-out;
`;
const Card = styled.div`
  position: relative; border-radius: 22px; overflow: hidden; cursor: pointer;
  transition: .35s; box-shadow: 0 6px 22px rgba(0,0,0,.14);
  &:hover { transform: translateY(-6px) scale(1.02); }
`;
const Thumb  = styled.img`width: 100%; height: 190px; object-fit: cover;`;
const Glass  = styled.div`
  position: absolute; inset: auto 0 0 0; padding: 1rem 1.25rem;
  background: rgba(255,255,255,.7); backdrop-filter: blur(14px);
`;
const Title  = styled.h3`margin: 0 0 .35rem; font-size: 1.15rem; color: #003b70;`;
const Chip   = styled.span`
  display: inline-block; font-size: .68rem; font-weight: 700; padding: .15rem .55rem;
  border-radius: 999px; background: ${p=>p.bg}; color: #fff; margin-right: .35rem;
`;
const Row    = styled.p`margin: .1rem 0; font-size: .9rem; color: #42526e;`;
const Price  = styled.div`font-weight: 700; font-size: 1.08rem; color: #175dff; margin-top: .45rem;`;
const Empty  = styled.p`grid-column: 1 / -1; text-align: center; font-weight: 600; color: #003b70;`;

/* helper */
const diffDays = (a,b)=>Math.max(1, Math.round((b-a)/864e5));

export default function CountryPage() {
  const { code } = useParams();                 /* â€œFRâ€, â€œESâ€, â€¦ */
  const nav      = useNavigate();

  /* default range â†’ next week */
  const [range,setRange] = useState([
    { key:"sel", startDate: addDays(new Date(),1), endDate:addDays(new Date(),8) }
  ]);

  const [hotels ,setHotels ] = useState([]);
  const [flights,setFlights] = useState([]);
  const [events ,setEvents ] = useState([]);

  /* fetch once */
  useEffect(()=>{
    (async()=>{
      const cfg = { headers:{Authorization:`Bearer ${localStorage.getItem("jwt")}`} };
      const [h,f,e] = await Promise.all([
        axios.get("/api/listings/hotels",  cfg),
        axios.get("/api/listings/flights", cfg),
        axios.get("/api/listings/events",  cfg)
      ]);
      setHotels (h.data.map(o=>({...o, pricePerNight:o.price})));
      setFlights(f.data);
      setEvents (e.data);
    })();
  },[]);

  /* build bundles whenever something changes */
  const bundles = useMemo(()=>{
    if(!range[0]) return [];
    const {startDate:endFrom, endDate:endTo} = range[0];
    const nights = diffDays(endFrom, endTo);

    const availHotels = hotels.filter(h =>
      h.country===code &&
      new Date(h.availableFrom) <= endFrom &&
      new Date(h.availableTo)   >= endTo );

    const availFlights = flights.filter(f =>
      f.country===code &&
      new Date(f.departureTime) >= endFrom &&
      new Date(f.arrivalTime)   <= endTo );

    const availEvents = events.filter(ev =>
      ev.country===code &&
      new Date(ev.eventDate) >= endFrom &&
      new Date(ev.eventDate) <= endTo );

    const out=[];
    availHotels.forEach(h=>{
      availFlights.forEach(f=>{
        availEvents.forEach(ev=>{
          out.push({
            id : `${h.id}-${f.id}-${ev.id}`,
            hotel:h, flight:f, event:ev, nights,
            total: nights*h.pricePerNight + f.price + ev.price,
            thumb: h.photoUrl ?? ev.photoUrl ?? f.photoUrl ?? "https://via.placeholder.com/400x250"
          });
        });
      });
    });
    return out.sort((a,b)=>a.total-b.total).slice(0,24);
  },[code,range,hotels,flights,events]);

  const heroImg = `https://source.unsplash.com/1600x600/?${code},landmark`;

  return (
    <>
      <Hero $img={heroImg}>{code}</Hero>

      <Filters>
        <strong style={{color:"#003b70",marginRight:"1rem"}}>Dates:</strong>
        <DateRange
          ranges={range}
          onChange={r=>r.selection && setRange([r.selection])}
          moveRangeOnFirstSelection={false}
          rangeColors={["#175dff"]}
          editableDateInputs
        />
      </Filters>

      <Grid>
        {bundles.length ? bundles.map(b=>(
          <Card key={b.id} onClick={()=>nav(`/packages/${b.id}`,{state:b})}>
            <Thumb src={b.thumb}/>
            <Glass>
              <Title>{b.hotel.title}</Title>
              <Row>
                {b.nights} nights Â· {b.hotel.city}<br/>
                <Chip bg="#175dff">âœˆ {b.flight.airline}</Chip>
                <Chip bg="#e92365">ðŸŽ« {b.event.title.length>18?b.event.title.slice(0,18)+"â€¦":b.event.title}</Chip>
              </Row>
              <Price>â‚¬ {b.total.toFixed(0)}</Price>
            </Glass>
          </Card>
        )) : <Empty>No matching bundles â€“ try another date window.</Empty>}
      </Grid>
    </>
  );
}
