/* src/pages/CountryPage.jsx */
import React, { useEffect, useState, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import axios from "axios";
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* ---------- styles ---------- */
const fade = keyframes`from{opacity:0}to{opacity:1}`;
const Hero = styled.section`
  background:url(${p=>p.img}) center/cover;
  height:260px; display:flex; align-items:center; justify-content:center;
  color:#fff; font-size:2.6rem; font-weight:800; text-shadow:0 2px 8px #0006;
`;
const Filters = styled.div`
  width:95%; max-width:1080px; margin:-40px auto 2.5rem;
  padding:1.25rem 1.75rem; border-radius:20px; backdrop-filter:blur(16px);
  background:rgba(255,255,255,.55); box-shadow:0 8px 28px rgba(0,0,0,.12);
  animation:${fade} .5s ease-out;
`;
const Grid = styled.section`
  display:grid; gap:2rem; max-width:1400px; margin:0 auto 4rem; padding:0 3rem;
  grid-template-columns:repeat(auto-fit,minmax(300px,1fr)); animation:${fade} .6s;
`;
const Card = styled.div`
  position:relative; border-radius:22px; overflow:hidden; cursor:pointer;
  transition:.35s; backdrop-filter:blur(4px);
  &:hover{transform:translateY(-6px) scale(1.02);}
`;
const Thumb = styled.img`width:100%; height:180px; object-fit:cover;`;
const Glass = styled.div`
  position:absolute; inset:auto 0 0 0; padding:1rem 1.2rem;
  background:rgba(255,255,255,.7); backdrop-filter:blur(14px);
`;
const Title  = styled.h3`margin:0 0 .25rem; font-size:1.1rem; color:#003b70;`;
const Row    = styled.p `margin:.1rem 0; font-size:.9rem; color:#42526e;`;
const Price  = styled.div`
  font-weight:700; font-size:1.05rem; color:#175dff; margin-top:.4rem;
`;

/* ---------- helpers ---------- */
const diffDays = (a,b)=>Math.max(1,Math.round((b-a)/864e5));

export default function CountryPage(){
  const { code }   = useParams();
  const navigate   = useNavigate();

  /* calendar default â†’ next week */
  const tomorrow = new Date(Date.now()+864e5);
  const nextWeek = new Date(Date.now()+7*864e5);
  const [range,setRange] = useState([{startDate:tomorrow,endDate:nextWeek,key:"sel"}]);

  /* raw listings */
  const [hotels, setHotels]   = useState([]);
  const [flights,setFlights]  = useState([]);
  const [events, setEvents]   = useState([]);

  useEffect(()=>{
    (async()=>{
      const tok = localStorage.getItem("jwt");
      const cfg = { headers:{Authorization:`Bearer ${tok}`} };
      const [h,f,e] = await Promise.all([
        axios.get("/api/listings/hotels",  cfg),
        axios.get("/api/listings/flights", cfg),
        axios.get("/api/listings/events",  cfg)
      ]);
      setHotels(h.data.map(o=>({...o, pricePerNight:o.price})));
      setFlights(f.data);
      setEvents(e.data);
    })();
  },[]);

  /* build bundles */
  const bundles = useMemo(()=>{
    if(!range[0]) return [];
    const from = range[0].startDate, to = range[0].endDate;
    const nights = diffDays(from,to);

    const availHotels  = hotels .filter(h => h.country===code &&
                         new Date(h.availableFrom)<=from && new Date(h.availableTo)>=to);

    const availFlights = flights.filter(f => f.country===code &&
                         new Date(f.departureTime)>=from && new Date(f.departureTime)<=to);

    const availEvents  = events .filter(ev=> ev.country===code &&
                         new Date(ev.eventDate)>=from && new Date(ev.eventDate)<=to);

    const out=[];
    availHotels.forEach(h=>{
      availFlights.forEach(f=>{
        availEvents.forEach(ev=>{
          out.push({
            id    :`${h.id}-${f.id}-${ev.id}`,
            hotel :h, flight:f, event:ev, nights,
            total :nights*h.pricePerNight + f.price + ev.price,
            thumb :h.photoUrl ?? ev.photoUrl ?? f.photoUrl
          });
        });
      });
    });
    return out.sort((a,b)=>a.total-b.total).slice(0,24);   // show more
  },[code,range,hotels,flights,events]);

  /* ---------- render ---------- */
  const heroImg = `https://source.unsplash.com/1600x600/?${code},landmark`;

  return (
    <>
      <Hero img={heroImg}>{code}</Hero>

      <Filters>
        <strong style={{color:"#003b70"}}>Dates</strong>
        <DateRange
          ranges={range}
          onChange={r=>r?.selection&&setRange([r.selection])}
          moveRangeOnFirstSelection={false}
          rangeColors={["#175dff"]}
          editableDateInputs
        />
      </Filters>

      <Grid>
        {bundles.length ? bundles.map(b=>(
          <Card key={b.id} onClick={()=>navigate(`/packages/${b.id}`,{state:b})}>
            <Thumb src={b.thumb||"https://via.placeholder.com/400x250"} />
            <Glass>
              <Title>{b.hotel.title}</Title>
              <Row>{b.nights} nights Â· {b.hotel.city}</Row>
              <Row>Flight âœˆ {b.flight.airline} Â· Event ðŸŽ« {b.event.title.slice(0,22)}</Row>
              <Price>â‚¬ {b.total.toFixed(0)}</Price>
            </Glass>
          </Card>
        )):<Row style={{gridColumn:"1/-1",fontWeight:600}}>No bundles â€“ adjust dates.</Row>}
      </Grid>
    </>
  );
}
