import React, { useEffect, useState, useMemo } from "react";
import { useParams }           from "react-router-dom";
import styled, { keyframes }   from "styled-components";
import axios                   from "axios";
import { DateRange }           from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* ─────────────────── style helpers ─────────────────── */
const fadeIn = keyframes`from{opacity:0;transform:translateY(10px)}
                         to  {opacity:1;transform:translateY(0)}`;
const Hero    = styled.section`
  background:url(${p=>p.img}) center/cover;
  height:240px;color:#fff;display:flex;align-items:center;
  justify-content:center;font-size:2.2rem;font-weight:800;`;
const Filters = styled.div`
  background:#fff;padding:1rem 1.5rem;border-radius:14px;
  box-shadow:0 4px 12px rgba(0,0,0,.12);display:flex;gap:2rem;
  width:90%;max-width:980px;margin:-32px auto 2rem;
  animation:${fadeIn}.4s ease-out;`;
const Grid    = styled.section`
  display:grid;gap:2rem;padding:0 2rem 3rem;max-width:1300px;
  margin:0 auto;grid-template-columns:repeat(auto-fit,minmax(280px,1fr));
  animation:${fadeIn}.5s ease-out;`;
const Card    = styled.div`
  border-radius:16px;overflow:hidden;background:#fff;
  box-shadow:0 6px 16px rgba(0,0,0,.12);`;
const Img     = styled.img`width:100%;height:150px;object-fit:cover;`;
const CBody   = styled.div`padding:1rem;display:flex;flex-direction:column;gap:.4rem;`;
const Price   = styled.span`font-weight:700;font-size:1.05rem;color:#175dff;`;
const Empty   = styled.p`grid-column:1/-1;text-align:center;font-weight:600;color:#003b70`;

/* ─────────────────── helpers ───────────────────────── */
const diffDays = (a,b)=>Math.max(1, Math.round((b - a)/(1000*3600*24)));

export default function CountryPage(){
  const { code } = useParams();

  /* calendar range */
  const [range,setRange]=useState([
    { startDate:new Date(), endDate:new Date(), key:"selection" }
  ]);

  /* raw API data */
  const [hotels ,setHotels ]=useState([]);
  const [flights,setFlights]=useState([]);
  const [events ,setEvents ]=useState([]);

  /* fetch once (with JWT in headers via global interceptor) */
  useEffect(()=>{
    (async()=>{
      const [h,f,e]=await Promise.all([
        axios.get("/api/listings/hotels"),
        axios.get("/api/listings/flights"),
        axios.get("/api/listings/events")
      ]);

      /* ---------- normalise names so UI logic stays simple ---------- */
      setHotels(h.data.map(o=>({ ...o, pricePerNight:o.price })));
      setFlights(f.data.map(o=>({
        ...o,
        departDate:o.departureTime,
        returnDate:o.arrivalTime
      })));
      setEvents(e.data);
    })();
  },[]);

  /* build packages when data *or* range changes */
  const packages=useMemo(()=>{
    if(!range.length || !range[0]) return [];

    const from=range[0].startDate, to=range[0].endDate;
    const nights = diffDays(from,to);

    const hFilt = hotels .filter(h => h.country===code &&
      new Date(h.availableFrom)<=from && new Date(h.availableTo)>=to);
    const fFilt = flights.filter(f => f.country===code &&
      new Date(f.departDate)>=from  && new Date(f.returnDate)<=to);
    const eFilt = events .filter(ev=> ev.country===code &&
      new Date(ev.eventDate)>=from && new Date(ev.eventDate)<=to);

    const out=[];
    hFilt.forEach(h=>{
      fFilt.forEach(f=>{
        eFilt.forEach(ev=>{
          out.push({
            id   :`${h.id}-${f.id}-${ev.id}`,
            hotel:h, flight:f, event:ev, nights,
            total : nights*h.pricePerNight + f.price + ev.price,
            thumb : h.photoUrl ?? f.photoUrl ?? ev.photoUrl
          });
        });
      });
    });
    return out.sort((a,b)=>a.total-b.total).slice(0,12);
  },[code,range,hotels,flights,events]);

/* ─────────────── render ─────────────── */
  const heroImg=`https://images.unsplash.com/1600x500/?${code}`;

  return(
    <>
      <Hero img={heroImg}>{code}</Hero>

      <Filters>
        <div>
          <strong>Dates</strong>
          <DateRange
            ranges={range}
            onChange={r=>setRange([r.selection])}
            moveRangeOnFirstSelection={false}
            rangeColors={["#175dff"]}
            editableDateInputs
          />
        </div>
      </Filters>

      <Grid>
        {packages.length
          ? packages.map(p=>(
              <Card key={p.id}>
                <Img src={p.thumb} alt="" />
                <CBody>
                  <div><strong>{p.hotel.title}</strong> + flight + event</div>
                  <div>{p.nights} nights · {p.hotel.city}</div>
                  <div>Flight: {p.flight.airline}</div>
                  <div>Event: {p.event.title}</div>
                  <Price>€ {p.total.toFixed(0)}</Price>
                </CBody>
              </Card>
            ))
          : <Empty>No matching packages – try different dates.</Empty>}
      </Grid>
    </>
  );
}
