import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { FaPlaneDeparture, FaHotel, FaCalendarCheck } from "react-icons/fa";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { attachJwt } from "../api/axios";

// ... (keep all existing styling helpers and constants)

export default function AuthPage() {
  const [mode, setMode] = useState("login");
  const [form, setForm] = useState({});
  const [msg, setMsg] = useState({ text: "", error: false });
  const navigate = useNavigate();

  const update = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const resetMsg = () => setMsg({ text: "", error: false });

  const validateSignup = () => {
    const errs = [];
    if (!form.firstName?.trim()) errs.push("First name is required");
    if (!form.lastName?.trim()) errs.push("Last name is required");

    if (!form.email?.match(/^[^@]+@[^@]+\.[^@]+$/))
      errs.push("Invalid email");

    if (!PASS_PATTERN.test(form.password || ""))
      errs.push("Password ≥8 chars, upper, lower, digit");

    if (form.password !== form.confirmPassword)
      errs.push("Passwords do not match");

    if (!form.age || +form.age < 18)
      errs.push("You must be at least 18");

    return errs;
  };

  const submit = async (e) => {
    e.preventDefault();
    resetMsg();
    try {
      if (mode === "login") {
        localStorage.removeItem("jwt"); // Clear token as previously added
        const { data: jwt } = await axios.post("/api/auth/login", form);
        localStorage.setItem("jwt", jwt);
        attachJwt(jwt);
        navigate("/home");
      } else if (mode === "signup") {
        const errs = validateSignup();
        if (errs.length) return setMsg({ text: errs.join("\\n"), error: true });

        const dto = {
          username: form.username,
          email: form.email,
          password: form.password,
          firstName: form.firstName,
          lastName: form.lastName,
          age: +form.age,
          role: form.role || "ROLE_USER",
        };
        await axios.post("/api/auth/signup", dto);
        setMsg({ text: "Account created! You can log in now.", error: false });
        setMode("login");
        setForm({});
      } else {
        if (!form.email?.trim()) return setMsg({ text: "Email is required", error: true });
        await axios.post("/api/auth/forgot-password", null, { params: { email: form.email } });
        setMsg({ text: "If that email exists, a reset link is on its way.", error: false });
      }
    } catch (err) {
      // Extract the message field from the error response
      const errorMessage = err.response?.data?.message || "Server error – please try again";
      setMsg({ text: errorMessage, error: true });
    }
  };

  const titles = { login: "Welcome back", signup: "Create account", forgot: "Forgot password" };
  const buttons = { login: "Log in", signup: "Sign up", forgot: "Send reset link" };

  const fields = () => {
    if (mode === "signup")
      return (
        <>
          <Input name="firstName" placeholder="First name" required onChange={update} />
          <Input name="lastName" placeholder="Last name" required onChange={update} />
          <Row>
            <Input name="age" type="number" min={18} placeholder="Age" required onChange={update} />
            <Select name="role" defaultValue="USER" onChange={update}>
              <option value="ROLE_USER">User</option>
              <option value="ROLE_ADMIN">Admin</option>
            </Select>
          </Row>
          <Input name="email" type="email" placeholder="Email" required onChange={update} />
          <Input name="username" placeholder="Username" required onChange={update} />
          <Input name="password" type="password" placeholder="Password" required onChange={update} />
          <Input name="confirmPassword" type="password" placeholder="Confirm password" required onChange={update} />
        </>
      );
    if (mode === "forgot")
      return <Input name="email" type="email" placeholder="Your account email" required onChange={update} />;
    return (
      <>
        <Input name="username" placeholder="Username" required onChange={update} />
        <Input name="password" type="password" placeholder="Password" required onChange={update} />
      </>
    );
  };

  return (
    <Wrapper>
      <ImageSide />
      <FormSide>
        <Brand>
          Bookify <FaPlaneDeparture />
        </Brand>
        <Tagline>Find flights, stays & events in one place</Tagline>

        <Card>
          <Title>{titles[mode]}</Title>
          {msg.text && <Msg error={msg.error.toString()}>{msg.text}</Msg>}

          <form onSubmit={submit}>
            {fields()}
            {mode === "login" && (
              <Small style={{ textAlign: "right", marginBottom: "1rem" }}>
                <LinkTxt
                  onClick={() => {
                    setMode("forgot");
                    setForm({});
                    resetMsg();
                  }}
                >
                  Forgot password?
                </LinkTxt>
              </Small>
            )}
            <Button type="submit">{buttons[mode]}</Button>
          </form>

          {mode !== "signup" && (
            <Small>
              New here?{" "}
              <LinkTxt
                onClick={() => {
                  setMode("signup");
                  setForm({});
                  resetMsg();
                }}
              >
                Create one
              </LinkTxt>
            </Small>
          )}
          {mode !== "login" && (
            <Small>
              Already have an account?{" "}
              <LinkTxt
                onClick={() => {
                  setMode("login");
                  setForm({});
                  resetMsg();
                }}
              >
                Log in
              </LinkTxt>
            </Small>
          )}
        </Card>

        <IconsRow>
          <FaPlaneDeparture />
          <FaHotel />
          <FaCalendarCheck />
        </IconsRow>
      </FormSide>
    </Wrapper>
  );
}
