import React, { useEffect, useState, useMemo } from "react";
import { useParams } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import api from "../api/axios";                       // ⬅️  NEW
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";

/* ---------- styling helpers ---------- */
const fadeIn = keyframes`from{opacity:0;transform:translateY(10px)}
                         to{opacity:1;transform:translateY(0)}`;
const Hero = styled.section`
  background:url(${p => p.img}) center/cover;
  height:240px;color:#fff;display:flex;align-items:center;justify-content:center;
  font-size:2.2rem;font-weight:800;
`;
const Filters = styled.div`
  background:#fff;padding:1rem 1.5rem;border-radius:14px;
  box-shadow:0 4px 12px rgba(0,0,0,.12);
  display:flex;gap:2rem;align-items:flex-start;width:90%;max-width:980px;
  margin:-32px auto 2rem;animation:${fadeIn}.4s ease-out;
`;
const Grid = styled.section`
  max-width:1300px;margin:0 auto;display:grid;gap:2rem;
  padding:0 2rem 3rem;grid-template-columns:repeat(auto-fit,minmax(280px,1fr));
  animation:${fadeIn}.5s ease-out;
`;
const Card = styled.div`
  border-radius:16px;overflow:hidden;background:#fff;
  box-shadow:0 6px 16px rgba(0,0,0,.12);
`;
const Img = styled.img`width:100%;height:150px;object-fit:cover;`;
const Body = styled.div`padding:1rem;display:flex;flex-direction:column;gap:.4rem;`;
const Price = styled.span`font-weight:700;font-size:1.05rem;color:#175dff;`;

const textDark = "#003b70";

/* ---------- helpers ---------- */
const diffDays = (a,b) =>
  Math.max(1, Math.round((b - a) / (1000 * 3600 * 24)));

export default function CountryPage() {
  const { code } = useParams();            // e.g. FR
  const [range, setRange] = useState([{
    startDate: new Date(),
    endDate:   new Date(),
    key: "sel"
  }]);

  /* listings state */
  const [hotels,  setHotels]  = useState([]);
  const [flights, setFlights] = useState([]);
  const [events,  setEvents]  = useState([]);

  /* one-time load */
  useEffect(() => {
    (async () => {
      const [h, f, e] = await Promise.all([
        api.get("/api/listings/hotels"),
        api.get("/api/listings/flights"),
        api.get("/api/listings/events")
      ]);
      setHotels(h.data); setFlights(f.data); setEvents(e.data);
    })();
  }, []);

  /* build packages in-browser */
  const packages = useMemo(() => {
    if (!hotels.length || !flights.length || !events.length) return [];
    const from = range[0].startDate, to = range[0].endDate;
    const nights = diffDays(from, to);

    const hFilt = hotels .filter(h  => h.country === code &&
      new Date(h.availableFrom) <= from && new Date(h.availableTo) >= to);
    const fFilt = flights.filter(f  => f.country === code &&
      new Date(f.departDate) >= from && new Date(f.returnDate) <= to);
    const eFilt = events .filter(ev => ev.country === code &&
      new Date(ev.eventDate) >= from && new Date(ev.eventDate) <= to);

    const out = [];
    hFilt.forEach(h =>
      fFilt.forEach(f =>
        eFilt.forEach(ev =>
          out.push({
            id:   `${h.id}-${f.id}-${ev.id}`,
            hotel:  h, flight: f, event: ev,
            nights,
            total: nights * h.pricePerNight + f.price + ev.price,
            thumb: h.photoUrl || f.photoUrl || ev.photoUrl ||
                   "https://via.placeholder.com/400x250"
          }))
      )
    );
    return out.sort((a,b) => a.total - b.total).slice(0, 12);
  }, [code, hotels, flights, events, range]);

  /* hero fallback */
  const heroImg = `https://images.unsplash.com/1600x500/?${code}&auto=format`;

  return (
    <>
      <Hero img={heroImg}>{code}</Hero>

      {/* filters */}
      <Filters>
        <div>
          <strong>Dates</strong>
          <DateRange
            ranges={range}
            onChange={item => setRange([item.selection])}
            moveRangeOnFirstSelection={false}
            rangeColors={["#175dff"]}
            editableDateInputs
          />
        </div>
        {/* slot for price slider / category toggles */}
      </Filters>

      {/* package grid */}
      <Grid>
        {packages.map(p => (
          <Card key={p.id}>
            <Img src={p.thumb} alt="package" />
            <Body>
              <div><strong>{p.hotel.name}</strong> + flight + event</div>
              <div>{p.nights} nights · {p.hotel.city}</div>
              <div>Flight: {p.flight.airline}</div>
              <div>Event: {p.event.title}</div>
              <Price>€ {p.total.toFixed(0)}</Price>
            </Body>
          </Card>
        ))}
        {!packages.length && (
          <p style={{ gridColumn: "1/-1", color: textDark }}>
            No matching packages. Try different dates.
          </p>
        )}
      </Grid>
    </>
  );
}
