// src/pages/CategoryPage.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import styled, { keyframes } from "styled-components";
import { FaCalendarAlt } from "react-icons/fa";
import Header from "../components/Header";

/* ─── animation ────────────────────────────────────────────── */
const fade = keyframes`
  from { opacity:0; transform:translateY(15px); }
  to   { opacity:1; transform:translateY(0);    }
`;

/* ─── styled bits ──────────────────────────────────────────── */
const Wrapper   = styled.div`padding:2rem;animation:${fade}.5s ease-out;`;
const FilterBar = styled.div`
  display:flex;align-items:center;gap:1rem;margin-bottom:1.5rem;
`;
const Select    = styled.select`padding:.5rem;font-size:1rem;`;
const DateInput = styled.input `padding:.5rem;font-size:1rem;`;

const Grid = styled.div`
  display:grid;gap:1.5rem;
  grid-template-columns:repeat(auto-fit,minmax(240px,1fr));
`;
const Card = styled.div`
  border-radius:12px;overflow:hidden;cursor:pointer;
  box-shadow:0 4px 12px rgba(0,0,0,.1);
  transition:transform .25s;
  &:hover{transform:translateY(-4px);}
`;
const Img  = styled.img`
  width:100%;height:160px;object-fit:cover;display:block;
`;
const Info = styled.div`padding:1rem;background:#fff;`;
const Title  = styled.h3 `margin:0 0 .4rem;font-size:1.2rem;`;
const Detail = styled.p   `margin:.2rem 0;font-size:.95rem;color:#333;`;

/* ─── component ────────────────────────────────────────────── */
export default function CategoryPage({ category }) {
  const [items,     setItems]     = useState([]);
  const [countries, setCountries] = useState([]);
  const [filterCountry, setFilterCountry] = useState("");
  const [filterDate,    setFilterDate]    = useState("");
  const navigate = useNavigate();

  /* secured load */
  useEffect(() => {
    (async () => {
      const token = localStorage.getItem("jwt");
      if (!token) { alert("Please log in."); return; }

      const res = await fetch(`/api/listings/${category}`, {
        headers:{ Authorization:`Bearer ${token}` }
      });

      if (res.status === 401) { alert("Session expired – log in again."); return; }
      if (!res.ok)            { throw new Error(await res.text()); }

      const data = await res.json();
      setItems(data);
      setCountries([...new Set(data.map(i => i.country))]);
    })().catch(err=>{
      console.error(err);
      alert(`Unable to load ${category}.`);
    });
  }, [category]);

  /* filter client side */
  const shown = items.filter(i =>
    (!filterCountry || i.country === filterCountry) &&
    (!filterDate    || new Date(filterDate) >= new Date(
         i.availableFrom ?? i.eventDate ?? i.departureTime))
  );

  return (
    <>
      <Header/>
      <Wrapper>
        {/* filters */}
        <FilterBar>
          <Select value={filterCountry}
                  onChange={e=>setFilterCountry(e.target.value)}>
            <option value="">All countries</option>
            {countries.map(c=><option key={c} value={c}>{c}</option>)}
          </Select>

          <DateInput type="date" value={filterDate}
                     onChange={e=>setFilterDate(e.target.value)} />
          <FaCalendarAlt/>
        </FilterBar>

        {/* catalogue */}
        <Grid>
          {shown.map(it=>(
            <Card key={it.id} onClick={()=>navigate(`/book/${category}/${it.id}`)}>
              {/* image only if present */}
              { (it.imageUrl || it.photo) && (
                <Img src={it.imageUrl || it.photo} alt={it.title}/>
              )}

              <Info>
                <Title>{it.title}</Title>

                {category==="flights" && (
                  <>
                    <Detail>{it.departure} → {it.arrival}</Detail>
                    <Detail>Seats left: {it.seatCapacity}</Detail>
                  </>
                )}

                {category==="hotels" && (
                  <>
                    <Detail>{it.city}</Detail>
                    <Detail>Rooms available: {it.totalRooms}</Detail>
                  </>
                )}

                {category==="events" && (
                  <>
                    <Detail>{new Date(it.eventDate).toLocaleDateString()}</Detail>
                    <Detail>Venue: {it.venue}</Detail>
                  </>
                )}
              </Info>
            </Card>
          ))}
        </Grid>
      </Wrapper>
    </>
  );
}
